INSERT INTO `email_templates_data` (`email_key`, `locale`, `assoc_type`, `assoc_id`, `subject`, `body`) VALUES
('SUBMISSION_ACK', 'en_US', 256, 1, 'Submission Acknowledgement', '{$authorName}:\r\n\r\nThank you for submitting the manuscript, "{$articleTitle}" to {$journalName}. With the online journal management system that we are using, you will be able to track its progress through the editorial process by logging in to the journal web site:\r\n\r\nManuscript URL: {$submissionUrl}\r\nUsername: {$authorUsername}\r\n\r\nIf you have any questions, please contact me. Thank you for considering this journal as a venue for your work.\r\n\r\n{$editorialContactSignature}'),
('REVIEW_REQUEST', 'en_US', 256, 1, 'Article Review Request', '{$reviewerName}:\r\n\r\nBased on your research interests and publications, I believe that you would serve as an excellent reviewer of the manuscript, "{$articleTitle}," which has been submitted to {$journalName}. The submission''s abstract is inserted below, and I hope that you will consider undertaking this important task for us.\r\n\r\nPlease log into the journal web site by {$weekLaterDate} to indicate whether you will undertake the review or not, as well as to access the submission and to record your review and recommendation. The web site is {$journalUrl}\r\n\r\nThe review itself is due {$reviewDueDate}.\r\n\r\nIf you do not have your username and password for the journal''s web site, you can use this link to reset your password (which will then be emailed to you along with your username). {$passwordResetUrl}\r\n\r\nSubmission URL: {$submissionReviewUrl}\r\n\r\nThank you for considering this request.\r\n\r\n{$editorialContactSignature}\r\n\r\n\r\n\r\n"{$articleTitle}"\r\n\r\n{$abstractTermIfEnabled}\r\n{$articleAbstract}'),
('REVIEW_REQUEST_ONECLICK', 'en_US', 256, 1, 'Invitation to Review for Psychological Research on Urban Society', 'Dear Dr. {$reviewerName}:\r\n\r\nI would like to invite you to review this manuscript, "{$articleId}" entitled {$articleTitle}," which has been submitted to {$journalName}. The submission''s abstract is inserted at the end of this letter for your reference.\r\n\r\nPlease use this link below to Accept or Decline this invitation. \r\n{$submissionReviewUrl}\r\n\r\nPlease log into the journal website by {$weekLaterDate} to indicate whether you will undertake the review or not, as well as to access the submission and to record your review and recommendation.\r\n\r\nPsychological Research on Urban Society is completely dependent on the expert opinion of reviewers such as yourself in order to continue publishing high-quality research articles for our readership.\r\n\r\nReviewing reciprocates the benefits you may have enjoyed in the past, or wish to enjoy in the future as an author. I hope that you will enjoy reviewing this paper, I also trust that you will be able to return your completed review within the given timeframe. We would need you to complete your review by {$reviewDueDate} to enable us to keep our review timetable on track.\r\n\r\nI would like to acknowledge that your expert review contributes to the high standards of the journal, and I thank you for your present and/or future participation.\r\n\r\nSincerely,\r\n\r\n{$editorialContactSignature}\r\n\r\n\r\n\r\n"{$articleTitle}"\r\n\r\n{$abstractTermIfEnabled}\r\n{$articleAbstract}'),
('REVIEW_ACK', 'en_US', 256, 1, 'Article Review Acknowledgement', '{$reviewerName}:\r\n\r\nThank you for completing the review of the submission, "{$articleTitle}," for {$journalName}. We appreciate your contribution to the quality of the work that we publish.\r\n\r\n{$editorialContactSignature}');
