(245, 19, 'Teaching files', 'Instructional', 'Immediately initiates a search based on the subject of the article or paper that connects users to free current and archival articles in the field from leading newspapers around the world.', 0, 0, 0, 0, 13),
(246, 19, 'Media reports', 'Media reports', 'Immediately initiates a search based on the subject of the article or paper that connects users to free current and archival articles in the field from leading newspapers around the world.', 0, 0, 0, 0, 14),
