INSERT INTO `rt_searches` (`search_id`, `context_id`, `title`, `description`, `url`, `search_url`, `search_post`, `seq`) VALUES
(1, 1, 'Google Scholar', 'Google Scholar enables specific searches of scholarly literature, including peer-reviewed papers, theses, books, pre-prints, abstracts, and technical reports. Content includes a range of publishers and aggregators with whom Google already has standing arrangements, e.g., the Association for Computing Machinery, IEEE, OCLC''s Open WorldCat library locator service, etc. Result displays will show different version clusters, citation analysis, and library location (currently books only).', 'http://scholar.google.com', 'http://scholar.google.com/scholar?ie=UTF-8&oe=UTF-8&hl=eng&as_sauthors={$formKeywords}', NULL, 0),
(2, 1, 'Intute: Science, Engineering & Technology', 'Intute: Science, Engineering and Technology is a free online service providing you with access to the very best Web resources for education and research, evaluated and selected by a network of subject specialists. It covers the physical sciences, engineering, computing, geography, mathematics and environmental science.  The database currently contains 33349 records.', 'http://www.intute.ac.uk/sciences/', 'http://www.intute.ac.uk/sciences/cgi-bin/search.pl?submit.x=0&submit.y=0&submit=Go&limit=0&subject=sciences&term1={$formKeywords}', NULL, 1),
(3, 1, 'OAIster (Open Archives Initiative research databases)', 'OAIster is a project of the University of Michigan Digital Library Production Services, and provides searching a wide variety of collections from a wide variety of institutions. These institutions have made the records of their digital resources available to access, and the project team has gathered and aggregated them into the OAIster service.', 'http://oaister.umdl.umich.edu/', 'http://oaister.umdl.umich.edu/cgi/b/bib/bib-idx?type=boolean&size=10&c=oaister&q1=&rgn1=entire+record&op2=and&q2=&rgn2=entire+record&op3=And&q3=&rgn3=title&op4=And&rgn4=author&op5=And&q5=&rgn5=subject&op6=And&rgn6=norm&q6=all+types&sort=author%2Fcreator&submit2=search&q4={$formKeywords}', NULL, 2),
(4, 1, 'DOE Information Bridge', 'The Information Bridge provides the open source to full-text and bibliographic records of Department of Energy (DOE) research and development reports in physics, chemistry, materials, biology, environmental sciences, energy technologies, engineering, computer and information science, renewable energy, and other topics.', 'http://www.osti.gov/bridge/index.jsp', 'http://www.osti.gov/bridge/basicsearch.jsp?act=Search&formname=basicsearch.jsp&review=1&SortBy=RELV&SortOrder=DESC&querytype=search&searchFor={$formKeywords}', NULL, 3),
(5, 1, 'Public Knowledge Project Open Archives Harvester', 'The PKP Open Archives Harvester is a free metadata indexing system developed by the Public Knowledge Project through its federally funded efforts to expand and improve access to research. The PKP OAI Harvester allows you to create a searchable index of the metadata from Open Archives Initiative-compliant archives, such as sites using Open Journal Systems or Open Conference Systems.', 'http://pkp.sfu.ca/harvester/', 'http://pkp.sfu.ca/harvester/search.php?limit=author&query={$formKeywords}', NULL, 4),
(6, 1, 'HighWire', 'HighWire is one of the highest-impact journals in the field of science, technology and medicine. As of March 2002 HighWire contained 11,785,877 articles in over 4,500 Medline journals and 405,385 free full text articles from 321 HighWire-based journals.', 'http://highwire.stanford.edu/', 'http://highwire.stanford.edu/cgi/searchresults?andorexactfulltext=and&resourcetype=1&src=hw&fulltext=&pubdate_year=&volume=&firstpage=&disp_type=&author1={$formKeywords}', NULL, 5),
(7, 1, 'NASA Technical Report Server', 'NASA Technical Report Server (NTRS) is to provide students, educators, and the public access to NASA''s technical literature. NTRS also collects scientific and technical information from sites external to NASA to broaden the scope of information available to users.', 'http://ntrs.nasa.gov/index.jsp?method=aboutntrs', 'http://ntrs.nasa.gov/search.jsp?N=0&Ntk=all&Ntx=mode%20matchall&Ntt={$formKeywords}', NULL, 6),
(8, 1, 'GrayLIT Network', 'GrayLIT Network provides a full-text search across the gray literature of multiple government agencies within a science portal of technical reports. It taps into the search engines of distributed gray literature collections, enabling the user to find information without first having to know the sponsoring agency.', 'http://graylit.osti.gov/', 'http://graylit.osti.gov/cgi-bin/dexplcgi', 'pp=all&INTERFACE=1WINDOW&FORM=DistributedSearch.html&COLLECTION=dticft&COLLECTION=jpl&COLLECTION=gpoinfo&COLLECTION=langley&MAXDOCS=50&QUERY={$formKeywords}', 7),
(9, 1, 'KOSMOI', 'KOSMOI includes educational articles, books, posters, & web links for all interested in the wonders of our Cosmos, on Science, Space, Technology, Nature, & Web Design. Updated daily.', 'http://kosmoi.com/', 'http://www.google.com/custom?sa=Search&cof=GIMP%3A%23cccccc%3BT%3A%23ffdd99%3BLW%3A468%3BALC%3Ared%3BL%3Ahttp%3A%2F%2Fencyclozine.com%2FPictures%2FBanner%2FEncycloZine2.jpg%3BGFNT%3A%23999999%3BLC%3A%23ffcc33%3BLH%3A60%3BBGC%3Ablack%3BAH%3Acenter%3BVLC%3A%23ffcc66%3BGL%3A2%3BGALT%3A%23ffffff%3BAWFID%3A29728ead1ae72975%3B&domains=EncycloZine.com%3BKosmoi.com%3BEluzions.com&sitesearch=Kosmoi.com&q={$formKeywords}', NULL, 8),
(10, 2, 'Google', 'Search for definitions using the popular Google search engine.', 'http://www.google.com/', 'http://www.google.com/search?hl=en&q=define%3A{$formKeywords}', NULL, 0),
(11, 2, 'Merriam-Webster Online', 'A dictionary based on the Webster 10th Collegiate edition, 1993, with updates added annually. More than 160,000 entries. Each word is accompanied by definition, pronunciation, usage, grammatical function, and a brief etymology. Includes a thesaurus. Misspellings return suggested spellings. The dictionary''s search system supports internal wildcards and right hand truncation searching. Includes an extensive pronunciation guide and sound files for the pronunciation of many terms.', 'http://www.merriam-webster.com/', 'http://www.merriam-webster.com/cgi-bin/dictionary?book=Dictionary&va={$formKeywords}', NULL, 1),
(12, 2, 'OneLook Dictionaries', 'OneLook is a meta-dictionary. Enter an English language word or acronym, and OneLook will search its index of 5,292,362 words in 934 dictionaries indexed in general and special interest dictionaries for the definition.', 'http://www.onelook.com/index.html', 'http://www.onelook.com/?ls=a&w={$formKeywords}', NULL, 2),
(13, 2, 'Wikipedia: The Free Encyclopedia', '"Wikipedia is a free content encyclopedia that is being written collaboratively by contributors from all around the world. The site is a WikiWiki, meaning that anyone ... can edit any article. ..." Without editorial oversight, the quality of the content on this site varies dramatically, but it is worth exploring. The English version has hundreds of thousands of entries. Spanish, French, and Esperanto are among the several other languages offered.', 'http://en.wikipedia.org/wiki/Main_Page', 'http://en.wikipedia.org/wiki/Special:Search?go=Go&fulltext=Search&search={$formKeywords}', NULL, 3),
(14, 2, 'Infoplease.com', 'This site includes contents of the Information Please Almanacs, a biography database, a dictionary, an atlas, and articles from the Columbia Encyclopedia. You can search these ready reference works together or separately or browse the Almanacs. There are feature stories each week covering topics in the news, with links to pertinent Almanac content and other Web resources.', 'http://www.infoplease.com', 'http://www.infoplease.com/search?fr=iptn&in=all&x=0&y=0&query={$formKeywords}', NULL, 4),
(15, 3, 'Science Books and Films', 'Science Books and Films is the AAAS review journal for science materials for all ages, including reviews and recommendations for books, videos, software, and web sites.', 'http://sbfonline.com/', 'http://sbfonline.com/sample/search.cgi?title_query_type=all&author_query_type&author=&other=&type=B&type=F&type=S&title={$formKeywords}', NULL, 0),
(16, 3, 'Google Print', 'Google is working with libraries at the University of Michigan, Harvard University, Stanford University, Oxford University and the New York Public Library to digitize books in their collections and make them accessible via Google Print, a massive scanning project that will bring millions of volumes of printed books into the Google Print database.  Click a book title and you''ll see the page of the book that has your search terms, your can search for more information within that specific book, find reviews, find related information, learn about the publisher, buy this book, and find nearby libraries that have it.', 'http://print.google.com', 'http://print.google.com/print?q={$formKeywords}', NULL, 1),
(17, 3, 'National Academy Press(NAP)', 'The National Academy Press (NAP) publishes over 200 books a year on a wide range of topics in science, engineering, and health, capturing the most authoritative views on important issues in science and health policy.', 'http://books.nap.edu/books/0309070317/html/177.html', 'http://search.nap.edu/nap-cgi/napsearch.cgi?term={$formKeywords}', NULL, 2),
(18, 3, 'E-STREAMS', 'E-STREAMS: Electronic reviews of Science & Technology References covering Engineering, Agriculture, Medicine and Science. Each issue contains 30+ STM reviews, covering new titles in Engineering, Agriculture, Medicine and Science. Each review is signed, and includes the email address of the reviewer. The reviews feature short TOCs, a list of contributors and bibliographic information.', 'http://www.e-streams.com/', 'http://www.e-streams.com/c3/cgi-bin/search.pl', 'boolean=AND&case=Insensitive&terms={$formKeywords}', 3),
(19, 3, 'Online books Page', 'Online books Page, edited by John Mark Ockerbloom, at the University of Pennsylvania.  This is an excellent starting point in the search for full-text books on the Internet. Over 20,000 English works in various formats available online at different sites. Entries may be searched by author or title. An interesting feature allows browsing by Library of Congress call number categories. New listings are added regularly and are listed on a separate web page at <a href="http://onlinebooks.library.upenn.edu/new.html">http://onlinebooks.library.upenn.edu/new.html</a>.', 'http://digital.library.upenn.edu/books/', 'http://onlinebooks.library.upenn.edu/webbin/book/search?tmode=words&title={$formKeywords}', NULL, 4),
(20, 3, 'Books-On-Line', 'A collection of over 32935 titles, although some are only excerpts. Browse by subject or search by author or title. Not all items are free.', 'http://www.books-on-line.com/', 'http://www.books-on-line.com/bol/KeyWordSearch.cfm?RowCount=50&Searchquery={$formKeywords}', NULL, 5),
(21, 3, 'Universal Library', 'The Universal Library is considerably more involved, and more ambitious -- it has amassed a collection of 100,000 e-books (some of them from Project Gutenberg), and is aiming for 1 million e-books by the end of 2006.', 'http://tera-3.ul.cs.cmu.edu/', 'http://tera-3.ul.cs.cmu.edu/cgi-bin/DBscripts/advsearch_db.cgi?perPage=25&listStart=0&author1=&subject1=Any&year1=&year2=&language1=Any&scentre=Any&search=Search&title1={$formKeywords}', NULL, 6),
(22, 4, 'Science.gov', 'Science.gov is a gateway to over 50 million pages of authoritative selected science information provided by U.S. government agencies, including research and development results.', 'http://www.science.gov', 'http://www.science.gov/scigov/search.html?expression={$formKeywords}', NULL, 0),
(23, 4, 'SciCentral', 'A directory of links to "today''s breaking science news." Browsable by topic, including biosciences, health sciences, physics, chemistry, earth and space, and engineering. Also includes links to related journals, databases, job opportunities, and conferences.', 'http://scicentral.com/', 'http://www.google.com/custom?client=pub-2641291926759270&forid=1&channel=1291454416&ie=ISO-8859-1&oe=ISO-8859-1&cof=GALT%3A%23008000%3BGL%3A1%3BDIV%3A%23FFFFFF%3BVLC%3A663399%3BAH%3Acenter%3BBGC%3AFFFFFF%3BLBGC%3AFFFFFF%3BALC%3A0000FF%3BLC%3A0000FF%3BT%3A000000%3BGFNT%3A9999FF%3BGIMP%3A9999FF%3BLH%3A50%3BLW%3A78%3BL%3Ahttp%3A%2F%2Fwww.scicentral.com%2Fimages%2Fscclogo_for_google.gif%3BS%3Ahttp%3A%2F%2Fwww.scicentral.com%3BFORID%3A1%3B&hl=en&q={$formKeywords}', NULL, 1),
(24, 5, 'Channel 4 Science', 'Channel 4''s science site covers a wide range of current scientific issues from science in society to science in medicine. The site is split into broad sections, each containing related topics, articles and programme information. There are resources at the end of the articles with listings of related Web Sites and reading material. The site also contains a helpful glossary and an "Ask an expert" facility for posing scientific queries to a body of scientific experts.', 'http://www.channel4.com/science/index.html', 'http://search.channel4.com/search?&sort=date%3AD%3AL%3Ad1&output=xml_no_dtd&ie=UTF-8&oe=UTF-8&client=standard_c4&proxystylesheet=standard_c4&q={$formKeywords}', NULL, 0),
(25, 5, 'Intute: Science, Engineering & Technology', 'Intute: Science, Engineering and Technology is a free online service providing you with access to the very best Web resources for education and research, evaluated and selected by a network of subject specialists. It covers the physical sciences, engineering, computing, geography, mathematics and environmental science.  The database currently contains 33349 records.', 'http://www.intute.ac.uk/sciences/', 'http://www.intute.ac.uk/sciences/cgi-bin/search.pl?submit.x=0&submit.y=0&submit=Go&limit=0&subject=sciences&term1={$formKeywords}', NULL, 1),
(26, 5, 'HighWire', 'HighWire is one of the highest-impact journals in the field of science, technology and medicine. As of March 2002 HighWire contained 11,785,877 articles in over 4,500 Medline journals and 405,385 free full text articles from 321 HighWire-based journals.', 'http://highwire.stanford.edu/', 'http://highwire.stanford.edu/cgi/searchresults?author1=&pubdate_year=&volume=&firstpage=&src=hw&hits=10&hitsbrief=25&resourcetype=1&andorexactfulltext=and&fulltext={$formKeywords}', NULL, 2),
(27, 5, 'FirstGov for Science', 'Science.gov is a gateway to authoritative selected science information provided by U.S. Government agencies, including research and development results.  It enables you to search 47 million pages in real time.', 'http://science.gov/', 'http://www.science.gov/search30/search.html?expression={$formKeywords}', NULL, 3),
(28, 5, 'DOE Information Bridge', 'The Information Bridge provides the open source to full-text and bibliographic records of Department of Energy (DOE) research and development reports in physics, chemistry, materials, biology, environmental sciences, energy technologies, engineering, computer and information science, renewable energy, and other topics.', 'http://www.osti.gov/bridge/index.jsp', 'http://www.osti.gov/bridge/basicsearch.jsp?act=Search&formname=basicsearch.jsp&review=1&SortBy=RELV&SortOrder=DESC&querytype=search&searchFor={$formKeywords}', NULL, 4),
(29, 5, 'CASI Technical Report Server', 'CASI Technical Report Server contains bibliographic citations and abstracts to unclassified documents announced in Scientific and technical aerospace reports, plus the document series produced by NASA''s predecessor, The National Advisory Committee for Aeronautics, and the NASA open literature file containing citations and abstracts to literature in the fields of aeronautics, space science, chemistry, engineering, geosciences, life sciences, mathematics, computer sciences, and physics.', 'http://www.sti.nasa.gov/RECONselect.html', 'http://ntrs.nasa.gov/index.cgi?method=search&limit=25&offset=0&mode=simple&order=DESC&keywords={$formKeywords}', NULL, 5),
(30, 5, 'GrayLIT Network', 'GrayLIT Network provides a full-text search across the gray literature of multiple government agencies within a science portal of technical reports. It taps into the search engines of distributed gray literature collections, enabling the user to find information without first having to know the sponsoring agency.', 'http://graylit.osti.gov/', 'http://graylit.osti.gov/cgi-bin/dexplcgi', 'pp=all&INTERFACE=1WINDOW&FORM=DistributedSearch.html&COLLECTION=dticft&COLLECTION=jpl&COLLECTION=gpoinfo&COLLECTION=langley&MAXDOCS=50&QUERY={$formKeywords}', 6),
(31, 5, 'KOSMOI', 'KOSMOI includes educational articles, books, posters, & web links for all interested in the wonders of our Cosmos, on Science, Space, Technology, Nature, & Web Design. Updated daily.', 'http://kosmoi.com/', 'http://www.google.com/custom?sa=Search&cof=GIMP%3A%23cccccc%3BT%3A%23ffdd99%3BLW%3A468%3BALC%3Ared%3BL%3Ahttp%3A%2F%2Fencyclozine.com%2FPictures%2FBanner%2FEncycloZine2.jpg%3BGFNT%3A%23999999%3BLC%3A%23ffcc33%3BLH%3A60%3BBGC%3Ablack%3BAH%3Acenter%3BVLC%3A%23ffcc66%3BGL%3A2%3BGALT%3A%23ffffff%3BAWFID%3A29728ead1ae72975%3B&domains=EncycloZine.com%3BKosmoi.com%3BEluzions.com&sitesearch=Kosmoi.com&q={$formKeywords}', NULL, 7),
(32, 5, 'PhilSci Archive', 'PhilSci Archive is an electronic archive for preprints in the philosophy of science.', 'http://philsci-archive.pitt.edu/', 'http://philsci-archive.pitt.edu/perl/search?_order=bytitle&abstract%2Fkeywords%2Ftitle_srchtype=ALL&_satisfyall=ALL&_action_search=Search&abstract%2Fkeywords%2Ftitle={$formKeywords}', NULL, 8),
(33, 6, 'Ingenta', 'Ingenta restricts access to full text articles. Although access to the full text costs money, the site can be used as a free index.', 'http://www.ingenta.com/', 'http://www.ingenta.com/isis/searching/Search/ingenta?database=1&title={$formKeywords}', NULL, 0),
(34, 6, 'ebrary', 'Independent researchers who do not have access to ebrary?s databases through their library may now set up an individual account for as little as $5. ebrary Discover spans multiple academic disciplines and provides anytime, anywhere access to over 20,000 authoritative titles including books in full-text, sheet music, reports and other authoritative documents from more than 175 leading publishers such as Cambridge University Press, Random House, Inc., and The McGraw-Hill Companies.', 'http://www.ebrary.com/corp/content.htm', 'http://shop.ebrary.com/Top?layout=search&f00=text&p01=&f01=subject&d=journal&l=en&sch=%A0%A0%A0%A0%A0Search%A0%A0%A0%A0%A0&frm=adv.x&p00={$formKeywords}', NULL, 1),
(35, 6, 'Questia', 'Questia provides access to over 47,000 books and 375,000 journal, magazine, and newspaper articles. All books and articles are available in their entirety - search every page. The subscription rate is ranging from 19.05 per month to 119.95 per year.', 'http://www.questia.com/', 'http://www.questia.com/SM.qst?act=search&keywordsSearchType=1000&mediaType=book&mediaType=journal&mediaType=magazine&mediaType=newspaper&mediaType=encyclopedia&mediaType=startpage&keywords={$formKeywords}', NULL, 2),
(36, 6, 'TheScientificWorld', 'TheScientificWorld offers sciBASE to give free access to a collection of databases of scientific, technical and medical research literature. sciBASE now also features immediate digital delivery of full text articles from over 700 journals produced by participating publishers, and sciBASE is particularly effective for users who do not have library support (non-mediated environments).', 'http://www.thescientificworld.com/', 'http://www.thescientificworld.com/SCIENTIFICWORLDJOURNAL/search/SearchResults.asp?From=Main&Terms={$formKeywords}', NULL, 3),
(37, 6, 'Wiley InterScience Pay-per-view Service', 'Wiley InterScience Pay-per-view affords instant, full-text access to an extensive collection of journal articles or book chapters available on Wiley InterScience without the need for a subscription. This service allows anyone to purchase access to individual journal articles or book chapters directly by using a credit card. Access is instant and available for 24 hours.', 'http://www3.interscience.wiley.com/aboutus/ppv-articleselect.html', 'http://www3.interscience.wiley.com/search/allsearch', 'allContentSearchForm=&mode=quicksearch&WISindexid1=WISall&WISsearch1={$formKeywords}', 4),
(38, 7, 'Scholarly Societies Project Meeting/Conference Announcement List', 'Scholarly Societies Project Meeting/Conference Announcement List is a searchable service provided by the University of Waterloo.', 'http://www.scholarly-societies.org/', 'http://ssp-search.uwaterloo.ca/cd.cfm?search_type=advanced&field1=any&boolean1=and&operator1=and&field2=any&textfield2=&boolean2=and&operator2=and&field3=any&textfield3=&boolean3=and&operator3=and&founded=none&&textfield1={$formKeywords}', NULL, 0),
(39, 8, 'Ask the Experts', 'Ask the Experts is provided by the Scientific American magazine. Questions and answers are archived and organized.', 'http://www.sciam.com/askexpert', 'http://www.google.com/search?q=site%3Asciam.com+%22ask+the+experts%22%2B+', NULL, 0),
(40, 8, 'EurekAlert!', 'EurekAlert! is an online press service created by the American Association for the Advancement of Science (AAAS). The primary goal of EurekAlert! is to provide a forum where research institutions, universities, government agencies, corporations and the like can distribute science-related news to reporters and news media. The secondary goal of EurekAlert! is to archive these press releases and make them available to the public in an easily retrievable system.', 'http://www.eurekalert.org/links.php', 'http://search.eurekalert.org/e3/query.html?col=ev3rel&qc=ev3rel&x=8&y=9&qt={$formKeywords}', NULL, 1),
(41, 8, 'Mad Science Net: The 24-hour exploding laboratory', 'Mad Science Net: The 24-hour exploding laboratory is a collective cranium of scientists providing answers to your questions.', 'http://www.madsci.org/', 'http://www.madsci.org/cgi-bin/search?Submit=Submit+Query&or=AND&words=1&index=MadSci+Archives&MAX_TOTAL=25&area=All+areas&grade=All+grades&query={$formKeywords}', NULL, 2),
(42, 8, 'JISCmail', 'The National Academic Mailing List Service, known as ''JISCmail'', is one of a number of JANET services provided by JANET(UK) (www.ja.net) and funded by the JISC (www.jisc.ac.uk) to benefit \nlearning, teaching and research communities. The Science and Technology Facilities Council (www.scitech.ac.uk) currently operates and develops the JISCmail service on behalf of JANET(UK).', 'http://www.jiscmail.ac.uk/about/index.htm', 'http://www.jiscmail.ac.uk/../cgi-bin/listsearcher.cgi?opt=listsearcher&listname=&alpha=&category=&chk_phrase=&chk_wds=ON&thecriteria={$formKeywords}', NULL, 3),
(43, 9, 'Research Channel Programs: Stanford Science Online Videos', 'ResearchChannel is a consortium of research universities and corporate research divisions dedicated to broadening the access to and appreciation of our individual and collective activities, ideas, and opportunities in basic and applied research.', 'http://www.researchchannel.org/program/displayseries.asp?collid=134', 'http://www.researchchannel.org/search/sitesearch.aspx?RecordsPerPage=5&Order=Rank&keywords=program&Query={$formKeywords}', NULL, 0),
(44, 9, 'National Science Digital Library', 'The National Science Digital Library (NSDL) was created by the National Science Foundation to provide organized access to high quality resources and tools that support innovations in teaching and learning at all levels of science, technology, engineering, and mathematics education.', 'http://nsdl.org/about', 'http://nsdl.org/search/?', 'formview=searchresults&verb=Search&s=0&n=10&boost%5B%5D=compoundTitle&boost%5B%5D=compoundDescription&q={$formKeywords}', 1),
(45, 9, 'Science Learning Network', 'Science Learning Network is a community of educators, students, schools, science museums, and other institutions demonstrating a new model for inquiry into. Contains a variety of inquiry-posed problems, information, demonstrations, and labs.', 'http://www.sln.org/', 'http://192.231.162.154:8080/query.html?col=first&ht=0&qp=&qs=&qc=&pw=600&ws=1&la=&qm=0&st=1&nh=25&lk=1&rf=0&oq=&rq=0&si=0&qt={$formKeywords}', NULL, 2),
(46, 9, 'Science, Mathematics, Engineering and Technology Education (SMETE)', 'Science, Mathematics, Engineering and Technology Education (SMETE) contains a searchable working prototype of a National Science, Mathematics, Engineering, and Technology Education Digital Library.', 'http://www.smete.org/', 'http://www.smete.org/smete/?path=/public/find/search_results.jhtml&_DARGS=/smete/public/find/index_body.jhtml&/smete/forms/FindLearningObjects.operation=search&_D:/smete/forms/FindLearningObjects.operation=%20&_D:/smete/forms/FindLearningObjects.keyword=%20&/smete/forms/FindLearningObjects.learningResourceType=&_D:/smete/forms/FindLearningObjects.learningResourceType=%20&/smete/forms/FindLearningObjects.grade=0-Any&_D:/smete/forms/FindLearningObjects.grade=%20&/smete/forms/FindLearningObjects.title=&_D:/smete/forms/FindLearningObjects.title=%20&/smete/forms/FindLearningObjects.author=&_D:/smete/forms/FindLearningObjects.author=%20&/smete/forms/FindLearningObjects.hostCollection=&_D:/smete/forms/FindLearningObjects.hostCollection=%20&/smete/forms/FindLearningObjects.afterYear=&_D:/smete/forms/FindLearningObjects.afterYear=%20&/smete/forms/FindLearningObjects.beforeYear=&_D:/smete/forms/FindLearningObjects.beforeYear=%20&&/smete/forms/FindLearningObjects.keyword={$formKeywords}', NULL, 3),
(47, 10, 'The Open Video Project', 'The Open Video project is a collection of public domain digital video available for research and other purposes.  The purpose of the Open Video Project is to collect and make available a repository of digitized video content for the digital video, multimedia retrieval, digital library, and other research communities.', 'http://www.open-video.org/index.php', 'http://www.open-video.org/results.php?search_field=all&terms={$formKeywords}', NULL, 0),
(48, 11, 'Access to Australian Government Information and Services', 'The Australian Government Entry Point offers comprehensive and integrated access to Australian Government information and services.  You will find a concentrated body of Australian Government information through this site. <a href="http://www.australia.gov.au">www.australia.gov.au</a> currently signposts over 700 Australian Government web sites, indexes more than 5,000,000 pages and uses both metadata and full text indexing to ensure it is a comprehensive government portal.', 'http://www.fed.gov.au', 'http://govsearch.australia.gov.au/search/search.cgi?collection=gov&form=au&query=&query_phrase=&query_or=&query_not=&meta_f_sand=&scope=&fscope=512&num_ranks=20&chksummary=chksummary&query_and={$formKeywords}', NULL, 0),
(49, 11, 'The Government of Canada', 'You can search across all government departments federal departments and agencies as well as provincial, territorial and municipal governments. There is a Departments and Agencies link, and the A to Z Index offers a keyword search to programmes and services. \n\nA recent development on Departmental sites is the inclusion of a "Proactive Disclosure" page, which outlines travel and hospitality expenses, disclosure of contracts, grants and awards. \n\nThe About Canada page includes links to Departments and Agencies, Structure of the Canadian Government, Commissions of Inquiry and more. \n\nIn addition to Department web sites, the government has been creating Portals which bring together information from across federal and provincial sources, and non-governmental organizations as well.', 'http://www.canada.gc.ca/main_e.html', 'http://search-recherche.gc.ca/cgi-bin/query?mss=canada%2Fen%2Fsimple&pg=q&enc=iso88591&site=main&bridge=&stt=&lowercaseq=&what=web&user=searchintranet&browser=N6E&kl=XX&op=a&q={$formKeywords}', NULL, 1),
(50, 11, 'Directgov', 'Directgov is a government service giving people access to the latest and widest range of public service information in one place on the Web and on Digital TV. Directgov uses the specifications formulated and consulted on through UK GovTalk.', 'http://www.direct.gov.uk', 'http://www.direct.gov.uk/AdvancedSearch/SearchResults/fs/en?NP=1&PO1=C&PI1=W&PF1=A&PG=1&RP=20&SC=__dgov_site&Z=1&PT1={$formKeywords}', NULL, 2),
(51, 11, 'Info4local.gov.uk', 'Information for local government from central government.  This site gives local authorities a quick and easy way to find relevant information on the web sites of central government departments, agencies and public bodies. It includes extensive information on implementing electronic government.', 'http://www.info4local.gov.uk', 'http://www.info4local.gov.uk/?view=Search+results&subjects=all&departmentsIds=0&informationTypesIds=all&query={$formKeywords}', NULL, 3),
(52, 11, 'The New Zealand government portal', 'The New Zealand government portal is a website providing search capability for, and links into the online and offline information and services of most government agencies.  This site is managed and maintained by the State Services Commission, a New Zealand government department.', 'http://www.govt.nz', 'http://www.govt.nz/search?type=spider&t=spider&q={$formKeywords}', NULL, 4),
(53, 11, 'Europa', 'Current information on the member countries of the European Union, including statistical data, policies, news, official documents, legal texts, and other publications.', 'http://europa.eu.int/index_en.htm', 'http://europa.eu.int/geninfo/query/resultaction.jsp?page=1', 'Collection=EuropaFull&ResultTemplate=/result_en.jsp&ResultCount=25&qtype=simple&ResultMaxDocs=200&DefaultLG=en&QueryText={$formKeywords}', 5),
(54, 11, 'The Global Legal Information Network (GLIN)', 'The Global Legal Information Network (GLIN) is a database of laws, regulations, judicial decisions, and other complementary legal sources contributed by governmental agencies and international organizations. These GLIN members contribute the official full texts of published documents to the database in their original language. Each document is accompanied by a summary in English and subject terms selected from the multilingual index to GLIN.', 'http://www.glin.gov', 'http://www.glin.gov/search.do?refineQuery=&offset=0&refineQueryType=&fromSearch=true&queryType=ALL&searchBtn=Search&includeAllFields=on&searchAll=on&sumLanguage=any&pubLanguage=any&pubJurisdiction=any&publicationJurisdictionExclude=false&searchPublicationDate=true&dateFromDay=01&dateFromMonth=01&dateFromYear=&dateToDay=01&dateToMonth=01&dateToYear=&subjTerm=&subjTermConjunction%5B0%5D=AND&subjTerm=&subjTermConjunction%5B1%5D=AND&subjTerm=&subjTermConjunction%5B2%5D=AND&subjTerm=&subjTermConjunction%5B3%5D=AND&subjTerm=&searchOrder=default&hitsPerPage=10&showSummary=on&queryString={$formKeywords}', NULL, 6),
(55, 11, 'FirstGov', 'FirstGov (U.S. federal and state) is a public-private partnership, led by a cross-agency board.', 'http://www.firstgov.gov/', 'http://www.firstgov.gov/fgsearch/index.jsp?db=www&st=AS&ms0=should&mt0=all&rn=2&parsed=true&x=2&y=8&mw0={$formKeywords}', NULL, 7),
(56, 11, 'Canada Sites', 'Canada Sites provides an information and services gateway run by the Government of Canada and each of the provinces.', 'http://www.canada.gc.ca/main_e.html', 'http://search-recherche.gc.ca/cgi-bin/query?mss=canada%2Fen%2Fsimple.html&enc=iso88591&pg=q&kl=en&site=main&q={$formKeywords}', NULL, 8),
(57, 12, 'Google News', 'Search news reports using the popular search engine Google''s news feature.', 'http://news.google.com/', 'http://news.google.com/news?hl=en&q={$formKeywords}', NULL, 0),
(58, 12, 'Daily Science News from NewScientist', 'Daily Science News from NewScientist provides science news updated throughout the day.', 'http://www.newscientist.com/news/', 'http://www.newscientist.com/search.ns?doSearch=true&query={$formKeywords}', NULL, 1),
(59, 12, 'Nature Science Update', 'Nature Science Update provides daily news stories written by the editors of the Nature Journal.', 'http://www.nature.com/nsu/', 'http://search.nature.com/search/?sp-x-9=cat&sp-q-9=NEWS&submit=go&sp-a=sp1001702d&sp-sfvl-field=subject%7Cujournal&sp-t=results&sp-x-1=ujournal&sp-p-1=phrase&sp-p=all&sp-q={$formKeywords}', NULL, 2),
(60, 12, 'Science News Online', 'Science News Online is one of the most useful science news magazines available online. The 75 year old weekly is putting a generous number of full-text articles on the Web each week, adding to a collection of articles archived from 1994 to the present.', 'http://www.sciencenews.org/', 'http://www.sciencenews.org/pages/search_results.asp?search={$formKeywords}', NULL, 3),
(61, 12, 'Newsdirectory', 'Newsdirectory is a comprehensive and searchable guide to the world''s English-language online media. Currently over 8,000 newspapers and magazines are listed, as well as more than 1,000 U.S. television broadcasters.', 'http://www.newsdirectory.com/', 'http://www.newsdirectory.com/hbSearch.php', 's={$formKeywords}& submit=Go', 4),
(62, 12, 'BBC Online: Science & Nature', 'The BBC online science and nature services which contain a wealth of information, resources and current news divided into clearly defined categories.  These categories include the human body, genes, prehistoric life, space and animals. A Hot Topics section explores the science behind the latest news.', 'http://www.bbc.co.uk/sn/', 'http://www.bbc.co.uk/cgi-bin/search/results.pl?uri=%2Fsn%2F&q={$formKeywords}', NULL, 5),
(63, 12, 'Energy Science News', 'Published by the Office of Science, U.S. Department of Energy.  The purpose of this newsletter is to inform scientists, engineers, educators, students, and the public about the progress of scientific research supported by the Office of Science.', 'http://www.eurekalert.org/doe/', 'http://search.eurekalert.org/e3/query.html?col=ev3rel+ev3feat&ht=0&qp=%2Binstitution%3ADOE+OR+%2Bfunder%3A%22US+Department+of+Energy%22&qs=&qc=ev3rel+ev3feat&pw=%25&ws=0&la=&si=1&fs=&ex=&rq=0&oq=&qm=0&ql=&st=1&nh=10&lk=1&rf=1&qt={$formKeywords}', NULL, 6),
(64, 12, 'ScienceDaily', 'Latest research news.', 'http://www.sciencedaily.com/index.htm', 'http://www.sciencedaily.com/search/?topic=all&dates=1995&dates=2005&sort=relevance&keyword={$formKeywords}', NULL, 7),
(65, 13, 'Google', 'Search using the popular Google search engine.', 'http://www.google.com/', 'http://www.google.com/search?q={$formKeywords}', NULL, 0),
(66, 13, 'Google Scholar', 'Google Scholar enables specific searches of scholarly literature, including peer-reviewed papers, theses, books, pre-prints, abstracts, and technical reports. Content includes a range of publishers and aggregators with whom Google already has standing arrangements, e.g., the Association for Computing Machinery, IEEE, OCLC''s Open WorldCat library locator service, etc. Result displays will show different version clusters, citation analysis, and library location (currently books only).', 'http://scholar.google.com', 'http://scholar.google.com/scholar?ie=UTF-8&oe=UTF-8&hl=en&q={$formKeywords}', NULL, 1),
(67, 13, 'Clusty the Clustering Engine', 'This search tool from Vivismo offers clustered results for a selection of searches.  Metasearch the whole web, or use tabs to search for news, gossip, images, orproducts via Bizrate or eBay.', 'http://clusty.com/about', 'http://clusty.com/search?query={$formKeywords}', NULL, 2),
(68, 13, 'Vivisimo', 'The award-winning Vivisimo clustering technology, developed by Carnegie Mellon research scientists, is unlocking the value of stored information at Fortune 500 companies, top websites, prestigious publishers and government agencies by categorizing research results on the fly into meaningful orders, thus achieving breakthrough improvement in access to relevant information.', 'http://vivisimo.com', 'http://vivisimo.com/search?tb=homepage&v%3Asources=Web&query={$formKeywords}', NULL, 3),
(69, 14, 'Google Scholar', 'Google Scholar enables specific searches of scholarly literature, including peer-reviewed papers, theses, books, pre-prints, abstracts, and technical reports. Content includes a range of publishers and aggregators with whom Google already has standing arrangements, e.g., the Association for Computing Machinery, IEEE, OCLC''s Open WorldCat library locator service, etc. Result displays will show different version clusters, citation analysis, and library location (currently books only).', 'http://scholar.google.com', 'http://scholar.google.com/scholar?ie=UTF-8&oe=UTF-8&hl=eng&as_sauthors={$formKeywords}', NULL, 0),
(70, 14, 'Collection of Computer Science Bibliographies', 'Contains about 1,200 bibliographies with over 1 million references, including over 100,000 web links to the full text of the article.', 'http://liinwww.ira.uka.de/bibliography/index.html#about', 'http://liinwww.ira.uka.de/csbib?online=&maxnum=200&sort=year&query={$formKeywords}', NULL, 1),
(71, 14, 'OAIster (Open Archives Initiative research databases)', 'OAIster is a project of the University of Michigan Digital Library Production Services, and provides searching a wide variety of collections from a wide variety of institutions. These institutions have made the records of their digital resources available to access, and the project team has gathered and aggregated them into the OAIster service.', 'http://oaister.umdl.umich.edu/', 'http://oaister.umdl.umich.edu/cgi/b/bib/bib-idx?type=boolean&size=10&c=oaister&q1=&rgn1=entire+record&op2=and&q2=&rgn2=entire+record&op3=And&q3=&rgn3=title&op4=And&rgn4=author&op5=And&q5=&rgn5=subject&op6=And&rgn6=norm&q6=all+types&sort=author%2Fcreator&submit2=search&q4={$formKeywords}', NULL, 2),
(72, 14, 'DOE Information Bridge', 'The Information Bridge provides the open source to full-text and bibliographic records of Department of Energy (DOE) research and development reports in physics, chemistry, materials, biology, environmental sciences, energy technologies, engineering, computer and information science, renewable energy, and other topics.', 'http://www.osti.gov/bridge/index.jsp', 'http://www.osti.gov/bridge/basicsearch.jsp?act=Search&formname=basicsearch.jsp&review=1&SortBy=RELV&SortOrder=DESC&querytype=search&searchFor={$formKeywords}', NULL, 3),
(73, 14, 'NCSTRL', 'NCSTRL is an international collection of computer science research reports and papers made available for non-commercial use from a number of participating institutions and archives. NCSTRL provides access to over 20,000 technical reports in computer science.', 'http://www.ncstrl.org:8900/ncstrl/index.html', 'http://www.ncstrl.org:8900/ncstrl/servlet/search?formname=simple&group=archive&sort=rank&fulltext={$formKeywords}', NULL, 4),
(74, 14, 'Public Knowledge Project Open Archives Harvester', 'The PKP Open Archives Harvester is a free metadata indexing system developed by the Public Knowledge Project through its federally funded efforts to expand and improve access to research. The PKP OAI Harvester allows you to create a searchable index of the metadata from Open Archives Initiative-compliant archives, such as sites using Open Journal Systems or Open Conference Systems.', 'http://pkp.sfu.ca/harvester/', 'http://pkp.sfu.ca/harvester/search.php?limit=author&query={$formKeywords}', NULL, 5),
(75, 14, 'arXiv.org', 'ArXiv is an e-print service in the fields of physics, mathematics, non-linear science and computer science.', 'http://arxiv.org/', 'http://arxiv.org/search?searchtype=all&query={$formKeywords}', NULL, 6),
(76, 14, 'devx', 'devx provides full text of more than 100,000 indexed and searchable articles, reviews, and more.', 'http://www.devx.com/', 'http://www.google.com/custom?cof=T%3A%23000000%3BLW%3A70%3BBIMG%3Ahttp%3A%2F%2Fwww.devxcom%2Fimages%2Fredesign%2Fbackground2.gif%3BALC%3A%23000099%3BL%3Ahttp%3A%2F%2Fwww.devx.com%2Fimages%2Fredesign%2Fnewlogosm2.gif%3BLC%3A%23000099%3BLH%3A70%3BBGC%3AFAFAE6%3BAH%3Aleft%3BVLC%3A%23000099%3BGL%3A0%3BAWFID%3Aaf259f950e64cb71%3B&domains=devx.com%3Bprojectcool.com%3Bvb2themax.com&sitesearch=devx.com&sa.x=16&sa.y=25&q={$formKeywords}', NULL, 7),
(77, 14, 'CiteSeer', 'CiteSeer makes available a broad, fully indexed, database of research papers from the various computer science fields.', 'http://citeseer.ist.psu.edu/cs', 'http://citeseer.ist.psu.edu/cs?submit=Search+Documents&cs=1&q={$formKeywords}', NULL, 8),
(78, 15, 'Google', 'Search for definitions using the popular Google search engine.', 'http://www.google.com/', 'http://www.google.com/search?hl=en&q=define%3A{$formKeywords}', NULL, 0),
(79, 15, 'Webopedia', 'Webopedia is an online dictionary with definitions of technical terms related to computers and the Internet.', 'http://www.webopedia.com/', 'http://search.internet.com/www.webopedia.com?IC_QueryDatabases=www.webopedia.com&IC_QueryText={$formKeywords}', NULL, 1),
(80, 15, 'WhatIs.com', 'WhatIs.com is an online encyclopedia of information technology terms.', 'http://whatis.techtarget.com/', 'http://whatis.techtarget.com/wsearchResults/1,290214,sid9,00.html?query={$formKeywords}', NULL, 2),
(81, 15, 'Free On-Line Dictionary of Computing', 'FOLDOC is a searchable dictionary of acronyms, jargon, programming languages, tools, architecture, operating systems, networking, theory, conventions, standards, mathematics, telecoms, electronics, institutions, companies, projects, products, and history related to computing.', 'http://foldoc.org/', 'http://foldoc.org/{$formKeywords}', NULL, 3),
(82, 15, 'Merriam-Webster Online', 'A dictionary based on the Webster 10th Collegiate edition, 1993, with updates added annually. More than 160,000 entries. Each word is accompanied by definition, pronunciation, usage, grammatical function, and a brief etymology. Includes a thesaurus. Misspellings return suggested spellings. The dictionary''s search system supports internal wildcards and right hand truncation searching. Includes an extensive pronunciation guide and sound files for the pronunciation of many terms.', 'http://www.merriam-webster.com/', 'http://www.merriam-webster.com/cgi-bin/dictionary?book=Dictionary&va={$formKeywords}', NULL, 4),
(83, 15, 'OneLook Dictionaries', 'OneLook is a meta-dictionary. Enter an English language \nword or acronym, and OneLook will search its index of 5,292,362 words in 934 \ndictionaries indexed in general and special interest dictionaries for the \ndefinition.', 'http://www.onelook.com/index.html', 'http://www.onelook.com/?ls=a&w={$formKeywords}', NULL, 5),
(84, 15, 'Wikipedia: The Free Encyclopedia', '"Wikipedia is a free content encyclopedia that is being written collaboratively by contributors from all around the world. The site is a WikiWiki, meaning that anyone ... can edit any article. ..." Without editorial oversight, the quality of the content on this site varies dramatically, but it is worth exploring. The English version has hundreds of thousands of entries. Spanish, French, and Esperanto are among the several other languages offered.', 'http://en.wikipedia.org/wiki/Main_Page', 'http://en.wikipedia.org/wiki/Special:Search?go=Go&fulltext=Search&search={$formKeywords}', NULL, 6),
(85, 15, 'Infoplease.com', 'This site includes contents of the Information Please Almanacs, a biography database, a dictionary, an atlas, and articles from the Columbia Encyclopedia. You can search these ready reference works together or separately or browse the Almanacs. There are feature stories each week covering topics in the news, with links to pertinent Almanac content and other Web resources.', 'http://www.infoplease.com', 'http://www.infoplease.com/search?fr=iptn&in=all&x=0&y=0&query={$formKeywords}', NULL, 7),
(86, 16, 'arXiv.org', 'ArXiv is an e-print service in the fields of physics, mathematics, non-linear science and computer science.', 'http://arxiv.org/', 'http://arxiv.org/search?searchtype=all&query={$formKeywords}', NULL, 0),
(87, 16, 'CiteSeer', 'CiteSeer makes available a broad, fully indexed, database of research papers from the various computer science fields.', 'http://citeseer.ist.psu.edu/cs', 'http://citeseer.ist.psu.edu/cs?submit=Search+Documents&cs=1&q={$formKeywords}', NULL, 1),
(88, 16, 'Collection of Computer Science Bibliographies', 'Contains about 1,200 bibliographies with over 1 million references, including over 100,000 web links to the full text of the article.', 'http://liinwww.ira.uka.de/bibliography/index.html#about', 'http://liinwww.ira.uka.de/csbib?online=&maxnum=200&sort=year&query={$formKeywords}', NULL, 2),
(89, 16, 'NCSTRL', 'NCSTRL is an international collection of computer science research reports and papers made available for non-commercial use from a number of participating institutions and archives. NCSTRL provides access to over 20,000 technical reports in computer science.', 'http://www.ncstrl.org:8900/ncstrl/index.html', 'http://www.ncstrl.org:8900/ncstrl/servlet/search?formname=simple&group=archive&sort=rank&fulltext={$formKeywords}', NULL, 3),
(90, 16, 'DOE Information Bridge', 'The Information Bridge provides the open source to full-text and bibliographic records of Department of Energy (DOE) research and development reports in physics, chemistry, materials, biology, environmental sciences, energy technologies, engineering, computer and information science, renewable energy, and other topics.', 'http://www.osti.gov/bridge/index.jsp', 'http://www.osti.gov/bridge/basicsearch.jsp?act=Search&formname=basicsearch.jsp&review=1&SortBy=RELV&SortOrder=DESC&querytype=search&searchFor={$formKeywords}', NULL, 4),
(91, 16, 'devx', 'devx provides full text of more than 100,000 indexed and searchable articles, reviews, and more.', 'http://www.devx.com/', 'http://www.google.com/custom?cof=T%3A%23000000%3BLW%3A70%3BBIMG%3Ahttp%3A%2F%2Fwww.devxcom%2Fimages%2Fredesign%2Fbackground2.gif%3BALC%3A%23000099%3BL%3Ahttp%3A%2F%2Fwww.devx.com%2Fimages%2Fredesign%2Fnewlogosm2.gif%3BLC%3A%23000099%3BLH%3A70%3BBGC%3AFAFAE6%3BAH%3Aleft%3BVLC%3A%23000099%3BGL%3A0%3BAWFID%3Aaf259f950e64cb71%3B&domains=devx.com%3Bprojectcool.com%3Bvb2themax.com&sitesearch=devx.com&sa.x=16&sa.y=25&q={$formKeywords}', NULL, 5),
(92, 16, 'HCI Bibliography: : Human-Computer Interaction Resources', 'The HCI Bibliography (HCIBIB) is a free-access bibliography on Human-Computer Interaction, with over 28,5000 records in a searchable database.', 'http://www.hcibib.org/', 'http://www.hcibib.org/gs.cgi?highlight=checked&action=Search&terms={$formKeywords}', NULL, 6),
(93, 17, 'Google Print', 'Google is working with libraries at the University of Michigan, Harvard University, Stanford University, Oxford University and the New York Public Library to digitize books in their collections and make them accessible via Google Print, a massive scanning project that will bring millions of volumes of printed books into the Google Print database.  Click a book title and you''ll see the page of the book that has your search terms, your can search for more information within that specific book, find reviews, find related information, learn about the publisher, buy this book, and find nearby libraries that have it.', 'http://print.google.com', 'http://print.google.com/print?q={$formKeywords}', NULL, 0),
(94, 17, 'Online books Page', 'Online books Page, edited by John Mark Ockerbloom, at the University of Pennsylvania.  This is an excellent starting point in the search for full-text books on the Internet. Over 20,000 English works in various formats available online at different sites. Entries may be searched by author or title. An interesting feature allows browsing by Library of Congress call number categories. New listings are added regularly and are listed on a separate web page at <a href="http://onlinebooks.library.upenn.edu/new.html">http://onlinebooks.library.upenn.edu/new.html</a>.', 'http://digital.library.upenn.edu/books/', 'http://onlinebooks.library.upenn.edu/webbin/book/search?tmode=words&title={$formKeywords}', NULL, 1),
(95, 17, 'Books-On-Line', 'A collection of over 32935 titles, although some are only excerpts. Browse by subject or search by author or title. Not all items are free.', 'http://www.books-on-line.com/', 'http://www.books-on-line.com/bol/KeyWordSearch.cfm?RowCount=50&Searchquery={$formKeywords}', NULL, 2),
(96, 17, 'O''Reilly Search', 'Search for books, articles, weblogs, conferences, and other resources published or maintained by O''Reilly & Associates, world-renowned publishers.', 'http://www.oreillynet.com/search/', 'http://catsearch.atomz.com/search/catsearch/?sp-a=sp1000a5a9&sp-f=ISO-8859-1&sp-t=cat_search&sp-advanced=1&sp-p=any&sp-d=custom&sp-date-range=-1&sp-q-1=&sp-x-1=collection&sp-c=10&sp-m=1&sp-s=0&sp-q={$formKeywords}', NULL, 3),
(97, 17, 'Safari Tech Books Online', 'O''Reilly''s online technical reference library.', 'http://safari.oreilly.com', 'http://safari.oreilly.com/search?_formName=searchForm&searchlistbox=Entire Site&searchtextbox={$formKeywords}', NULL, 4),
(98, 17, 'Universal Library', 'The Universal Library is considerably more involved, and more ambitious -- it has amassed a collection of 100,000 e-books (some of them from Project Gutenberg), and is aiming for 1 million e-books by the end of 2006.', 'http://tera-3.ul.cs.cmu.edu/', 'http://tera-3.ul.cs.cmu.edu/cgi-bin/DBscripts/advsearch_db.cgi?perPage=25&listStart=0&author1=&subject1=Any&year1=&year2=&language1=Any&scentre=Any&search=Search&title1={$formKeywords}', NULL, 5);
INSERT INTO `rt_searches` (`search_id`, `context_id`, `title`, `description`, `url`, `search_url`, `search_post`, `seq`) VALUES
(99, 18, 'Collection of Computer Science Bibliographies', 'Collection of Computer Science Bibliographies - A searchable collection of bibliographies from various sources, covering most aspects of computer science. Access references to journal articles, conference papers, & technical reports. The collection currently contains more than 2 millions of references (mostly to journal articles, conference papers and technical reports), clustered in about 1500 bibliographies, and consists of more than 2.3 GBytes (530MB gzipped) of BibTeX entries. More than 600 000 references contain cross references to citing or cited publications. More than 1 million of references contain URLs to an online version of the paper. Abstracts are available for more than 800 000 entries. There are more than 2000 links to other sites carrying bibliographic information.', 'http://liinwww.ira.uka.de/bibliography/index.html', 'http://liinwww.ira.uka.de/mpsbib?field=&year=&sincd=&before=&results=citation&maxnum=40&online=on&query={$formKeywords}', NULL, 0),
(100, 18, 'Engineering E-journal Search Engine (EESE)', 'The Engineering E-journal Search Engine (EESE) searches the full text of over 100 free and full text engineering e-Journals.', 'http://www.eevl.ac.uk/eese/index.html', 'http://www.eevl.ac.uk/eevl-cross-results.htm?tab=all&eevl_sect=eevl&exact=on&method=All&searchst={$formKeywords}', NULL, 1),
(101, 18, 'CompuScience', '"CompuScience" is a bibliographic database covering literature in the field of computer science and computer technology. Dating back to 1936, the database comprises about 656.378 citations.  CompuScience is integrated in io-port.net, the portal for computer science in Germany and Europe.  CompuScience contains the abstracts of the ACM Computing Reviews and the Computer Science Section of Zentralblatt fÃ¼r Mathematik (MATH database) and the abstracts of over 95 selected journals in this area. Citations contain bibliographic information and indexing terms. Many records also include an abstract. Most citations are classified according to the Computing Reviews Classification Scheme of ACM.', 'http://www.fiz-informationsdienste.de/en/DB/compusci/index.html', 'http://www.io-port.net/ioport2004/singlefieldSearch.do?feld1=fulltext&eingabe2=&eingabe3=&eingabe4=&eingabe5=&sortierung=jahr&query=&Abschicken=Suchen&eingabe1={$formKeywords}', NULL, 2),
(102, 19, 'Ingenta', 'Ingenta restricts access to full text articles. Although access to the full text costs money, the site can be used as a free index.', 'http://www.ingenta.com/', 'http://www.ingenta.com/isis/searching/Search/ingenta?database=1&title={$formKeywords}', NULL, 0),
(103, 19, 'ebrary', 'Independent researchers who do not have access to ebrary_ databases through their library may now set up an individual account for as little as $5. ebrary Discover spans multiple academic disciplines and provides anytime, anywhere access to over 20,000 authoritative titles including books in full-text, sheet music, reports and other authoritative documents from more than 175 leading publishers such as Cambridge University Press, Random House, Inc., and The McGraw-Hill Companies.', 'http://www.ebrary.com/corp/content.htm', 'http://shop.ebrary.com/Top?layout=search&f00=text&p01=&f01=subject&d=journal&l=en&sch=%A0%A0%A0%A0%A0Search%A0%A0%A0%A0%A0&frm=adv.x&p00={$formKeywords}', NULL, 1),
(104, 19, 'Questia', 'Questia provides access to over 47,000 books and 375,000 journal, magazine, and newspaper articles. All books and articles are available in their entirety - search every page.  The subscription rate is ranging from 19.05 per month to 119.95 per year.', 'http://www.questia.com/', 'http://www.questia.com/SM.qst?act=search&keywordsSearchType=1000&mediaType=book&mediaType=journal&mediaType=magazine&mediaType=newspaper&mediaType=encyclopedia&mediaType=startpage&keywords={$formKeywords}', NULL, 2),
(105, 19, 'Wiley InterScience Pay-per-view Service', 'Wiley InterScience Pay-per-view affords instant, full-text access to an extensive collection of journal articles or book chapters available on Wiley InterScience without the need for a subscription. This service allows anyone to purchase access to individual journal articles or book chapters directly by using a credit card. Access is instant and available for 24 hours.', 'http://www3.interscience.wiley.com/aboutus/ppv-articleselect.html', 'http://www3.interscience.wiley.com/search/allsearch', 'allContentSearchForm=&mode=quicksearch&WISindexid1=WISall&WISsearch1={$formKeywords}', 3),
(106, 20, 'GrayLIT Network', 'GrayLIT Network provides a full-text search across the gray literature of multiple government agencies within a science portal of technical reports. It taps into the search engines of distributed gray literature collections, enabling the user to find information without first having to know the sponsoring agency.', 'http://graylit.osti.gov/', 'http://graylit.osti.gov/cgi-bin/dexplcgi', 'pp=all&INTERFACE=1WINDOW&FORM=DistributedSearch.html&COLLECTION=dticft&COLLECTION=jpl&COLLECTION=gpoinfo&COLLECTION=langley&MAXDOCS=50&QUERY={$formKeywords}', 0),
(107, 21, 'Canadian Patents Database', 'Canadian Patent Database lets you access over 75 years of patent descriptions and images. You can search, retrieve and study more than 1,500,000 patent documents', 'http://patents1.ic.gc.ca/intro-e.html', 'http://patents1.ic.gc.ca/fcgi-bin/patquery_eo_el?-t=_&-l=ibm.html&-m=50&-c=/cpoti/prod/verity/coll/cpd&ERROR_MSG=Query failed with rc =&GENERAL={$formKeywords}', NULL, 0),
(108, 21, 'Europe''s Network of Patent Databases (Esp@cenet)', 'Europe''s Network of Patent Databases (Esp@cenet) is a free service for accessing patent information. Four database files may be searched: (1)European Patents (most recent 24 months, with PDF files); (2) PCT(WO) patents (most recent 24 months, with PDF files); (3) worldwide patents (coverage depends on country, mostly back to 1960s or 1970s); and (4) Japanese patents (1976-present). Since 1970, each patent family in the collection has a representative document with a searchable English-language title and abstract.', 'http://ep.espacenet.com', 'http://v3.espacenet.com/results?AB=fiber&sf=q&FIRST=1&CY=ep&LG=en&DB=EPODOC&st=AB&Submit=SEARCH&=&=&=&=&=&kw={$formKeywords}', NULL, 1),
(109, 21, 'USPTO', 'USPTO databases cover the period from 1 January 1976 to the most recent weekly issue date (usually each Tuesday). Also has the ability to order patents within this system.', 'http://www.uspto.gov/', 'http://patft.uspto.gov/netacgi/nph-Parser?Sect1=PTO2&Sect2=HITOFF&p=1&u=%2Fnetahtml%2Fsearch-bool.html&r=0&f=S&l=50&FIELD1=&co1=AND&TERM2=&FIELD2=&d=ptxt&TERM1={$formKeywords}', NULL, 2),
(110, 22, 'The International Organization for Standardization (ISO)', 'The International Organization for Standardization (ISO) is a worldwide federation of national standards bodies from some 140 countries. ISO''s work has resulted in some 12,000 International Standards, representing more than 300,000 pages in English and French.', 'http://www.iso.ch/iso/en/CatalogueListPage.CatalogueList', 'http://www.iso.ch/iso/en/CombinedQueryResult.CombinedQueryResult?queryString={$formKeywords}', NULL, 0),
(111, 22, 'Request For Comments (RFCs)', 'The Requests for Comments (RFC) document series is a set of technical and organizational notes about the Internet (originally the ARPANET), beginning in 1969. Memos in the RFC series discuss many aspects of computer networking, including protocols, procedures, programs, and concepts, as well as meeting notes, opinions, and sometimes humor.', 'http://www.rfc-editor.org/rfcsearch.html', 'http://www.rfc-editor.org/cgi-bin/rfcsearch.pl?opt=All+Fields&num=25&filefmt=txt&search_doc=search_all&match_method=prefix&sort_method=newer&abstract=absoff&keywords=keyoff&format=ftp&searchwords={$formKeywords}', NULL, 1),
(112, 22, 'XML.com', 'An exhaustive site on all things XML (Extensible Markup Language), from schemas and style to the Semantic Web. Largely oriented toward power users, but includes useful FAQs for newcomers. Searchable. From the O''Reilly & Associates publishing house. ', 'http://www.xml.com/', 'http://search.atomz.com/search/?sp-a=sp1000a5a9&sp-f=ISO-8859-1&sp-t=cat_search&sp-x-1=collection&sp-q-1=xml&sp-q={$formKeywords}', NULL, 2),
(113, 23, 'All Conferences Directory', 'All Conferences Directory is a searchable database of Computer Science and Technology conferences that organizes conferences by category and offers information regarding paper submission deadlines.', 'http://all-conferences.com/Computers/', 'http://www.allconferences.com/Search/search.php3?Search={$formKeywords}', NULL, 0),
(114, 23, 'DB and LP: Conferences and Workshops', 'DB and LP: Conferences and Workshops contains a list of computer science conferences and Workshops: past, present and future.', 'http://www.informatik.uni-trier.de/~ley/db/conf/index.a.html', 'http://www.google.com/search?hl=en&lr=&q=site%3Awww.informatik.uni-trier.de%2F+conf+{$formKeywords}', NULL, 1),
(115, 23, 'Netlib Conferences Databases', 'The Netlib Conferences Databases contains information about upcoming conferences, lectures, and other meetings relevant to the fields of mathematics and computer science.', 'http://www.netlib.org/confdb/confsearch.html', 'http://netlib2.cs.utk.edu/cgi-bin/csearch/confdisp.pl?ip_address=160.36.58.108&ip_name=netlib-old.cs.utk.edu&tcp_port=8123&database_name=%2Fusr%2Flocal%2Fwais%2Findexes%2Fconfdb&search_term={$formKeywords}', NULL, 2),
(116, 23, 'TechCalendar', 'TechCalendar is a searchable/browseable event directory, with categories such as: Internet/Online, Communications, Software & Services, Vertical Markets, Computing Platforms, and Computing Industry.', 'http://www.techweb.com/calendar/', 'http://www.tsnn.com/partner/results/results_techweb.cfm?city=&select=-1&country=-1&classid=120&Month=-1&subject={$formKeywords}', NULL, 3),
(117, 24, 'The Open Video Project', 'The Open Video project is a collection of public domain digital video available for research and other purposes.  The purpose of the Open Video Project is to collect and make available a repository of digitized video content for the digital video, multimedia retrieval, digital library, and other research communities.', 'http://www.open-video.org/index.php', 'http://www.open-video.org/results.php?search_field=all&terms={$formKeywords}', NULL, 0),
(118, 25, 'National Science Digital Library', 'The National Science Digital Library (NSDL) was created by the National Science Foundation to provide organized access to high quality resources and tools that support innovations in teaching and learning at all levels of science, technology, engineering, and mathematics education.', 'http://nsdl.org/about', 'http://nsdl.org/search/?formview=searchresults&verb=Search&s=0&n=10&boost%5B%5D=compoundTitle&boost%5B%5D=compoundDescription&q={$formKeywords}', NULL, 0),
(119, 25, 'Science, Mathematics, Engineering and Technology Education (SMETE)', 'Science, Mathematics, Engineering and Technology Education (SMETE) contains a searchable working prototype of a National Science, Mathematics, Engineering, and Technology Education Digital Library.', 'http://www.smete.org/', 'http://www.smete.org/smete/?path=/public/find/search_results.jhtml&_DARGS=/smete/public/find/index_body.jhtml&/smete/forms/FindLearningObjects.operation=search&_D:/smete/forms/FindLearningObjects.operation=%20&_D:/smete/forms/FindLearningObjects.keyword=%20&/smete/forms/FindLearningObjects.learningResourceType=&_D:/smete/forms/FindLearningObjects.learningResourceType=%20&/smete/forms/FindLearningObjects.grade=0-Any&_D:/smete/forms/FindLearningObjects.grade=%20&/smete/forms/FindLearningObjects.title=&_D:/smete/forms/FindLearningObjects.title=%20&/smete/forms/FindLearningObjects.author=&_D:/smete/forms/FindLearningObjects.author=%20&/smete/forms/FindLearningObjects.hostCollection=&_D:/smete/forms/FindLearningObjects.hostCollection=%20&/smete/forms/FindLearningObjects.afterYear=&_D:/smete/forms/FindLearningObjects.afterYear=%20&/smete/forms/FindLearningObjects.beforeYear=&_D:/smete/forms/FindLearningObjects.beforeYear=%20&&/smete/forms/FindLearningObjects.keyword={$formKeywords}', NULL, 1),
(120, 26, 'Access to Australian Government Information and Services', 'The Australian Government Entry Point offers comprehensive and integrated access to Australian Government information and services.  You will find a concentrated body of Australian Government information through this site. <a href="http://www.australia.gov.au">www.australia.gov.au</a> currently signposts over 700 Australian Government web sites, indexes more than 5,000,000 pages and uses both metadata and full text indexing to ensure it is a comprehensive government portal.', 'http://www.fed.gov.au', 'http://govsearch.australia.gov.au/search/search.cgi?collection=gov&form=au&query=&query_phrase=&query_or=&query_not=&meta_f_sand=&scope=&fscope=512&num_ranks=20&chksummary=chksummary&query_and={$formKeywords}', NULL, 0),
(121, 26, 'The Government of Canada', 'You can search across all government departments federal departments and agencies as well as provincial, territorial and municipal governments. There is a Departments and Agencies link, and the A to Z Index offers a keyword search to programmes and services. \n\nA recent development on Departmental sites is the inclusion of a "Proactive Disclosure" page, which outlines travel and hospitality expenses, disclosure of contracts, grants and awards. \n\nThe About Canada page includes links to Departments and Agencies, Structure of the Canadian Government, Commissions of Inquiry and more. \n\nIn addition to Department web sites, the government has been creating Portals which bring together information from across federal and provincial sources, and non-governmental organizations as well.', 'http://www.canada.gc.ca/main_e.html', 'http://search-recherche.gc.ca/cgi-bin/query?mss=canada%2Fen%2Fsimple&pg=q&enc=iso88591&site=main&bridge=&stt=&lowercaseq=&what=web&user=searchintranet&browser=N6E&kl=XX&op=a&q={$formKeywords}', NULL, 1),
(122, 26, 'Directgov', 'Directgov is a government service giving people access to the latest and widest range of public service information in one place on the Web and on Digital TV. Directgov uses the specifications formulated and consulted on through UK GovTalk.', 'http://www.direct.gov.uk', 'http://www.direct.gov.uk/AdvancedSearch/SearchResults/fs/en?NP=1&PO1=C&PI1=W&PF1=A&PG=1&RP=20&SC=__dgov_site&Z=1&PT1={$formKeywords}', NULL, 2),
(123, 26, 'Info4local.gov.uk', 'Information for local government from central government.  This site gives local authorities a quick and easy way to find relevant information on the web sites of central government departments, agencies and public bodies. It includes extensive information on implementing electronic government.', 'http://www.info4local.gov.uk', 'http://www.info4local.gov.uk/?view=Search+results&subjects=all&departmentsIds=0&informationTypesIds=all&query={$formKeywords}', NULL, 3),
(124, 26, 'The New Zealand government portal', 'The New Zealand government portal is a website providing search capability for, and links into the online and offline information and services of most government agencies.  This site is managed and maintained by the State Services Commission, a New Zealand government department.', 'http://www.govt.nz', 'http://www.govt.nz/search?type=spider&t=spider&q={$formKeywords}', NULL, 4),
(125, 26, 'Europa', 'Current information on the member countries of the European Union, including statistical data, policies, news, official documents, legal texts, and other publications.', 'http://europa.eu.int/index_en.htm', 'http://europa.eu.int/geninfo/query/resultaction.jsp?page=1', 'Collection=EuropaFull&ResultTemplate=/result_en.jsp&ResultCount=25&qtype=simple&ResultMaxDocs=200&DefaultLG=en&QueryText={$formKeywords}', 5),
(126, 26, 'The Global Legal Information Network (GLIN)', 'The Global Legal Information Network (GLIN) is a database of laws, regulations, judicial decisions, and other complementary legal sources contributed by governmental agencies and international organizations. These GLIN members contribute the official full texts of published documents to the database in their original language. Each document is accompanied by a summary in English and subject terms selected from the multilingual index to GLIN.', 'http://www.glin.gov', 'http://www.glin.gov/search.do?refineQuery=&offset=0&refineQueryType=&fromSearch=true&queryType=ALL&searchBtn=Search&includeAllFields=on&searchAll=on&sumLanguage=any&pubLanguage=any&pubJurisdiction=any&publicationJurisdictionExclude=false&searchPublicationDate=true&dateFromDay=01&dateFromMonth=01&dateFromYear=&dateToDay=01&dateToMonth=01&dateToYear=&subjTerm=&subjTermConjunction%5B0%5D=AND&subjTerm=&subjTermConjunction%5B1%5D=AND&subjTerm=&subjTermConjunction%5B2%5D=AND&subjTerm=&subjTermConjunction%5B3%5D=AND&subjTerm=&searchOrder=default&hitsPerPage=10&showSummary=on&queryString={$formKeywords}', NULL, 6),
(127, 26, 'FirstGov', 'FirstGov (U.S. federal and state) is a public-private partnership, led by a cross-agency board.', 'http://www.firstgov.gov/', 'http://www.firstgov.gov/fgsearch/index.jsp?db=www&st=AS&ms0=should&mt0=all&rn=2&parsed=true&x=2&y=8&mw0={$formKeywords}', NULL, 7),
(128, 26, 'Canada Sites', 'Canada Sites provides an information and services gateway run by the Government of Canada and each of the provinces.', 'http://www.canada.gc.ca/main_e.html', 'http://search-recherche.gc.ca/cgi-bin/query?mss=canada%2Fen%2Fsimple.html&enc=iso88591&pg=q&kl=en&site=main&q={$formKeywords}', NULL, 8),
(129, 27, 'Google News', 'Search news reports using the popular search engine Google''s news feature.', 'http://news.google.com/', 'http://news.google.com/news?hl=en&q={$formKeywords}', NULL, 0),
(130, 27, 'Daily Science News from NewScientist', 'Daily Science News from NewScientist provides science news updated throughout the day.', 'http://www.newscientist.com/news/', 'http://www.newscientist.com/search.ns?doSearch=true&query={$formKeywords}', NULL, 1),
(131, 27, 'Nature Science Update', 'Nature Science Update provides daily news stories written by the editors of the Nature Journal.', 'http://www.nature.com/nsu/', 'http://search.nature.com/search/?sp-x-9=cat&sp-q-9=NEWS&submit=go&sp-a=sp1001702d&sp-sfvl-field=subject%7Cujournal&sp-t=results&sp-x-1=ujournal&sp-p-1=phrase&sp-p=all&sp-q={$formKeywords}', NULL, 2),
(132, 27, 'Science News Online', 'Science News Online is one of the most useful science news magazines available online. The 75 year old weekly is putting a generous number of full-text articles on the Web each week, adding to a collection of articles archived from 1994 to the present.', 'http://www.sciencenews.org/search.asp', 'http://www.sciencenews.org/pages/search_results.asp?search={$formKeywords}', NULL, 3),
(133, 27, 'Scientific American Archive', 'Scientific American Archive is the online science and technology resource offering access to every page and every issue of Scientific American magazine from 1993 to the present.', 'http://www.sciamdigital.com/', 'http://www.sciamdigital.com/index.cfm?fa=Search.ViewSearchForItemResultList&AUTHOR_CHAR=&TITLE_CHAR=&FullText_CHAR={$formKeywords}', NULL, 4),
(134, 27, 'Newsdirectory', 'Newsdirectory is a comprehensive and searchable guide to the world''s English-language online media. Currently over 8,000 newspapers and magazines are listed, as well as more than 1,000 U.S. television broadcasters.', 'http://www.newsdirectory.com/', 'http://www.newsdirectory.com/hbSearch.php', 's={$formKeywords}& submit=Go', 5),
(135, 28, 'Google', 'Search using the popular Google search engine.', 'http://www.google.com/', 'http://www.google.com/search?q={$formKeywords}', NULL, 0),
(136, 28, 'Google Scholar', 'Google Scholar enables specific searches of scholarly literature, including peer-reviewed papers, theses, books, pre-prints, abstracts, and technical reports. Content includes a range of publishers and aggregators with whom Google already has standing arrangements, e.g., the Association for Computing Machinery, IEEE, OCLC''s Open WorldCat library locator service, etc. Result displays will show different version clusters, citation analysis, and library location (currently books only).', 'http://scholar.google.com', 'http://scholar.google.com/scholar?ie=UTF-8&oe=UTF-8&hl=en&q={$formKeywords}', NULL, 1),
(137, 28, 'Clusty the Clustering Engine', 'This search tool from Vivismo offers clustered results for a selection of searches.  Metasearch the whole web, or use tabs to search for news, gossip, images, orproducts via Bizrate or eBay.', 'http://clusty.com/about', 'http://clusty.com/search?query={$formKeywords}', NULL, 2),
(138, 28, 'Vivisimo', 'The award-winning Vivisimo clustering technology, developed by Carnegie Mellon research scientists, is unlocking the value of stored information at Fortune 500 companies, top websites, prestigious publishers and government agencies by categorizing research results on the fly into meaningful orders, thus achieving breakthrough improvement in access to relevant information.', 'http://vivisimo.com', 'http://vivisimo.com/search?tb=homepage&v%3Asources=Web&query={$formKeywords}', NULL, 3),
(139, 29, 'Google Scholar', 'Google Scholar enables specific searches of scholarly literature, including peer-reviewed papers, theses, books, pre-prints, abstracts, and technical reports. Content includes a range of publishers and aggregators with whom Google already has standing arrangements, e.g., the Association for Computing Machinery, IEEE, OCLC''s Open WorldCat library locator service, etc. Result displays will show different version clusters, citation analysis, and library location (currently books only).', 'http://scholar.google.com', 'http://scholar.google.com/scholar?ie=UTF-8&oe=UTF-8&hl=eng&as_sauthors={$formKeywords}', NULL, 0),
(140, 29, 'OAIster (Open Archives Initiative research databases)', 'OAIster is a project of the University of Michigan Digital Library Production Services, and provides searching a wide variety of collections from a wide variety of institutions. These institutions have made the records of their digital resources available to access, and the project team has gathered and aggregated them into the OAIster service.', 'http://oaister.umdl.umich.edu/', 'http://oaister.umdl.umich.edu/cgi/b/bib/bib-idx?type=boolean&size=10&c=oaister&q1=&rgn1=entire+record&op2=and&q2=&rgn2=entire+record&op3=And&q3=&rgn3=title&op4=And&rgn4=author&op5=And&q5=&rgn5=subject&op6=And&rgn6=norm&q6=all+types&sort=author%2Fcreator&submit2=search&q4={$formKeywords}', NULL, 1),
(141, 29, 'Scirus', 'Scirus searches both free and access controlled journal sources. It currently covers the Web, ScienceDirect, MEDLINE on BioMedNet, Beilstein on ChemWeb, Neuroscion, BioMed Central and Patents from the USPTO.', 'http://www.scirus.com/', 'http://www.scirus.com/search_simple/?frm=simple&dsmem=on&dsweb=on&wordtype_1=phrase&query_1={$formKeywords}', NULL, 2),
(142, 29, 'Public Knowledge Project Open Archives Harvester', 'The PKP Open Archives Harvester is a free metadata indexing system developed by the Public Knowledge Project through its federally funded efforts to expand and improve access to research. The PKP OAI Harvester allows you to create a searchable index of the metadata from Open Archives Initiative-compliant archives, such as sites using Open Journal Systems or Open Conference Systems.', 'http://pkp.sfu.ca/harvester/', 'http://pkp.sfu.ca/harvester/search.php?limit=author&query={$formKeywords}', NULL, 3),
(143, 29, 'Centers for Disease Control and Prevention (CDC)', 'The CDC Web site provides access to the full text of MMWR and other CDC publications and data archives. Publications are searchable through CDC Wonder.', 'http://www.cdc.gov/', 'http://www.cdc.gov/search.do?action=search&x=0&y=0&queryText={$formKeywords}', NULL, 4),
(144, 29, 'HighWire', 'HighWire is one of the highest-impact journals in the field of science, technology and medicine. As of March 2002 HighWire contained 11,785,877 articles in over 4,500 Medline journals and 405,385 free full text articles from 321 HighWire-based journals.', 'http://highwire.stanford.edu/lists/freeart.dtl', 'http://highwire.stanford.edu/cgi/searchresults?andorexactfulltext=and&resourcetype=1&src=hw&fulltext=&pubdate_year=&volume=&firstpage=&disp_type=&author1={$formKeywords}', NULL, 5),
(145, 29, 'MEDLINE/PubMed', 'MEDLINE is the National Library of Medicine''s database of references to more than 11 million articles published in 4300 biomedical journals.', 'http://www.ncbi.nlm.nih.gov/pubmed/', 'http://www.ncbi.nlm.nih.gov/entrez/query.fcgi?db=PubMed&orig_db=PubMed&cmd=search&cmd_current=&query_key=1&term={$formKeywords}', NULL, 6),
(146, 29, 'NetPrints', 'NetPrints provides a place for authors to archive their completed studies before, during, or after peer review by other agencies. Its scope is original research into clinical medicine and health.', 'http://clinmed.netprints.org/search.dtl', 'http://www.google.com/search?hl=en&q=site%3Aclinmed.netprints.org+{$formKeywords}', NULL, 7),
(147, 29, 'Intute: Health & Life Sciences', 'The Health and Life Sciences pages of Intute is a free online service providing you with access to the very best web resources for education and research, evaluated and selected by a network of subject specialists. There are over 31,000 resource descriptions listed here that are freely accessible for keyword searching or browsing.\n\nThis service was formerly known as BIOME.', 'http://www.intute.ac.uk/healthandlifesciences/', 'http://www.intute.ac.uk/healthandlifesciences/cgi-bin/search.pl?submit.x=20&submit.y=16&submit=Go&limit=0&subject=healthandlifesciences&term1={$formKeywords}', NULL, 8),
(148, 30, 'Google', 'Search for definitions using the popular Google search engine.', 'http://www.google.com/', 'http://www.google.com/search?hl=en&q=define%3A{$formKeywords}', NULL, 0),
(149, 30, 'ADAM Medical Encyclopedia', 'The ADAM Medical Encyclopedia includes over 4,000 articles about diseases, tests, symptoms, injuries, and surgeries. It also contains an extensive library of medical photographs and illustrations.', 'http://www.nlm.nih.gov/medlineplus/encyclopedia.html', 'http://search.nlm.nih.gov/medlineplus/query?DISAMBIGUATION=true&FUNCTION=search&SHOWTOPICS=5&SERVER2=server2&SERVER1=server1&ASPECT=-1&START=0&END=0&x=29&y=10&PARAMETER={$formKeywords}', NULL, 1),
(150, 30, 'Life Sciences Dictionary from BioTech', 'Life Sciences Dictionary from BioTech comprises 8,300+ terms relating to biochemistry, biotechnology, botany, cell biology and genetics, as well as selective entries on ecology, limnology, pharmacology, toxicology and medicine.', 'http://biotech.icmb.utexas.edu/search/dict-search.html', 'http://biotech.icmb.utexas.edu/search/dict-search2.html?bo1=AND&search_type=normal&def=&word={$formKeywords}', NULL, 2),
(151, 30, 'MedTerms Medical Dictionary Index', 'MedTerms Medical Dictionary Index is a doctor-produced encyclopedic medical dictionary, almost daily updated. Can be browsed by using the A to Z Index above or by typing the term in the Search Box below and click.', 'http://www.medicinenet.com/script/main/AlphaIdx.asp?li=MNI&p=A_DICT', 'http://www.medicinenet.com/script/main/srchCont.asp?li=MNI&ArtTypeID=DICT&op=MM&SRC={$formKeywords}', NULL, 3),
(152, 30, 'Merriam-Webster Online', 'A dictionary based on the Webster 10th Collegiate edition, 1993, with updates added annually. More than 160,000 entries. Each word is accompanied by definition, pronunciation, usage, grammatical function, and a brief etymology. Includes a thesaurus. Misspellings return suggested spellings. The dictionary''s search system supports internal wildcards and right hand truncation searching. Includes an extensive pronunciation guide and sound files for the pronunciation of many terms.', 'http://www.merriam-webster.com/', 'http://www.merriam-webster.com/cgi-bin/dictionary?book=Dictionary&va={$formKeywords}', NULL, 4),
(153, 30, 'OneLook Dictionaries', 'OneLook is a meta-dictionary. Enter an English language word or acronym, and OneLook will search its index of 5,292,362 words in 934 dictionaries indexed in general and special interest dictionaries for the definition.', 'http://www.onelook.com/index.html', 'http://www.onelook.com/?ls=a&w={$formKeywords}', NULL, 5),
(154, 30, 'On-Line Medical Dictionary', 'The dictionary started in early 1997 and has grown, to contain over 46,000 definitions totalling 17.5 megabytes. Entries are cross-referenced to each other and to related resources elsewhere on the net. It is freely available on the Internet via the World-Wide Web.  OMD is a searchable dictionary created by Dr Graham Dark and contains terms relating to biochemistry, cell biology, chemistry, medicine, molecular biology, physics, plant biology, radiobiology, science and technology. It includes: acronyms, jargon, theory, conventions, standards, institutions, projects, eponyms, history, in fact anything to do with medicine or science.', 'http://cancerweb.ncl.ac.uk/cgi-bin/omd?', 'http://cancerweb.ncl.ac.uk/cgi-bin/omd?query={$formKeywords}', NULL, 6),
(155, 30, 'Wikipedia: The Free Encyclopedia', '"Wikipedia is a free content encyclopedia that is being written collaboratively by contributors from all around the world. The site is a WikiWiki, meaning that anyone ... can edit any article. ..." Without editorial oversight, the quality of the content on this site varies dramatically, but it is worth exploring. The English version has hundreds of thousands of entries. Spanish, French, and Esperanto are among the several other languages offered.', 'http://en.wikipedia.org/wiki/Main_Page', 'http://en.wikipedia.org/wiki/Special:Search?go=Go&fulltext=Search&search={$formKeywords}', NULL, 7),
(156, 30, 'Infoplease.com', 'This site includes contents of the Information Please Almanacs, a biography database, a dictionary, an atlas, and articles from the Columbia Encyclopedia. You can search these ready reference works together or separately or browse the Almanacs. There are feature stories each week covering topics in the news, with links to pertinent Almanac content and other Web resources.', 'http://www.infoplease.com', 'http://www.infoplease.com/search?fr=iptn&in=all&x=0&y=0&query={$formKeywords}', NULL, 8),
(157, 31, 'Centers for Disease Control and Prevention (CDC)', 'The CDC Web site provides access to the full text of MMWR and other CDC publications and data archives. Publications are searchable through CDC Wonder.', 'http://www.cdc.gov/', 'http://www.cdc.gov/search.do?action=search&x=0&y=0&queryText={$formKeywords}', NULL, 0),
(158, 31, 'ClinicalTrials.gov', 'ClinicalTrials.gov (National Institutes of Health) provides information for patients about clinical research studies.', 'http://www.clinicaltrials.gov', 'http://search.nhsdirect.nhs.uk/kbroker/nhsdirect/nhsdirect/search.lsim?hs=0&sm=0&ha=1054&sc=nhsdirect&mt=0&sb=0&nh=3&qt={$formKeywords}', NULL, 1),
(159, 31, 'Scirus', 'Scirus searches both free and access controlled journal sources. It currently covers the Web, ScienceDirect, MEDLINE on BioMedNet, Beilstein on ChemWeb, Neuroscion, BioMed Central and Patents from the USPTO.', 'http://www.scirus.com/', 'http://www.scirus.com/search_simple/?frm=simple&dsmem=on&dsweb=on&wordtype_1=phrase&query_1={$formKeywords}', NULL, 2),
(160, 31, 'Emedicine', 'Emedicine is a directory of free online medical articles and up-to-date, searchable, peer-reviewed medical textbooks for physicians, veterinarians, medical students, physician assistants, nurse practitioners, nurses and the public.', 'http://www.emedicine.com/', 'http://www.emedicine.com/cgi-bin/foxweb.exe/searchengine@/em/searchengine?boolean=and&book=all&maxhits=100&HiddenURL=&query={$formKeywords}', NULL, 3),
(161, 31, 'HealthWeb', 'Provides links to evaluated information resources selected by librarians and information professionals at academic medical centers in the Midwest. The goal is to meet the health information needs of both health care professionals and consumers. A collaborative project of the health sciences libraries of the Greater Midwest Region (GMR), of the National Network of Libraries of Medicine (NN/LM), and of the Committee for Institutional Cooperation.', 'http://www.healthweb.org/', 'http://www.healthweb.org/quicksearch_results5.cfm?StartRow=1&maxrows=25&Criteria_required=You+Must+enter+a+Keyword&where=1&criteria={$formKeywords}', NULL, 4),
(162, 31, 'HighWire', 'HighWire is one of the highest-impact journals in the field of science, technology and medicine. As of March 2002 HighWire contained 11,785,877 articles in over 4,500 Medline journals and 405,385 free full text articles from 321 HighWire-based journals.', 'http://highwire.stanford.edu/lists/freeart.dtl', 'http://highwire.stanford.edu/cgi/searchresults?andorexactfulltext=and&resourcetype=1&author1=&src=ml&disp_type=&fulltext={$formKeywords}', NULL, 5),
(163, 31, 'MEDLINE/PubMed', 'MEDLINE is the National Library of Medicine''s database of references to more than 11 million articles published in 4300 biomedical journals.', 'http://www.ncbi.nlm.nih.gov/pubmed/', 'http://www.ncbi.nlm.nih.gov/entrez/query.fcgi?db=PubMed&orig_db=PubMed&cmd=search&cmd_current=&query_key=1&term={$formKeywords}', NULL, 6),
(164, 31, 'MEDLINEplus', 'MEDLINEplus will direct you to information to help answer health questions. It brings together, by health topic, authoritative information from the world''s largest medical library, the National Library of Medicine at the National Institutes of Health. Designed for both health professionals and consumers, this service provides extensive information about specific diseases and conditions.', 'http://medlineplus.gov/', 'http://search.nlm.nih.gov/medlineplus/query?DISAMBIGUATION=true&FUNCTION=search&SERVER2=server2&SERVER1=server1&PARAMETER={$formKeywords}', NULL, 7),
(165, 31, 'NetPrints', 'NetPrints provides a place for authors to archive their completed studies before, during, or after peer review by other agencies. Its scope is original research into clinical medicine and health.', 'http://clinmed.netprints.org/search.dtl', 'http://www.google.com/search?hl=en&q=site%3Aclinmed.netprints.org+{$formKeywords}', NULL, 8),
(166, 31, 'Intute: Health & Life Sciences', 'The Health and Life Sciences pages of Intute is a free online service providing you with access to the very best web resources for education and research, evaluated and selected by a network of subject specialists. There are over 31,000 resource descriptions listed here that are freely accessible for keyword searching or browsing.', 'http://www.intute.ac.uk/healthandlifesciences/', 'http://www.intute.ac.uk/healthandlifesciences/cgi-bin/search.pl?submit.x=20&submit.y=16&submit=Go&limit=0&subject=healthandlifesciences&term1={$formKeywords}', NULL, 9),
(167, 32, 'The Open Video Project', 'The Open Video project is a collection of public domain digital video available for research and other purposes.  The purpose of the Open Video Project is to collect and make available a repository of digitized video content for the digital video, multimedia retrieval, digital library, and other research communities.', 'http://www.open-video.org/index.php', 'http://www.open-video.org/results.php?search_field=all&terms={$formKeywords}', NULL, 0),
(168, 33, 'Google Print', 'Google is working with libraries at the University of Michigan, Harvard University, Stanford University, Oxford University and the New York Public Library to digitize books in their collections and make them accessible via Google Print, a massive scanning project that will bring millions of volumes of printed books into the Google Print database.  Click a book title and you''ll see the page of the book that has your search terms, your can search for more information within that specific book, find reviews, find related information, learn about the publisher, buy this book, and find nearby libraries that have it.', 'http://print.google.com', 'http://print.google.com/print?q={$formKeywords}', NULL, 0),
(169, 33, 'Online books Page', 'Online books Page, edited by John Mark Ockerbloom, at the University of Pennsylvania.  This is an excellent starting point in the search for full-text books on the Internet. Over 20,000 English works in various formats available online at different sites. Entries may be searched by author or title. An interesting feature allows browsing by Library of Congress call number categories. New listings are added regularly and are listed on a separate web page at <a href="http://onlinebooks.library.upenn.edu/new.html">http://onlinebooks.library.upenn.edu/new.html</a>.', 'http://digital.library.upenn.edu/books/', 'http://onlinebooks.library.upenn.edu/webbin/book/search?tmode=words&title={$formKeywords}', NULL, 1),
(170, 33, 'Books-On-Line', 'A collection of over 32935 titles, although some are only excerpts. Browse by subject or search by author or title. Not all items are free.', 'http://www.books-on-line.com/', 'http://www.books-on-line.com/bol/KeyWordSearch.cfm?RowCount=50&Searchquery={$formKeywords}', NULL, 2),
(171, 33, 'National Academy Press(NAP)', 'The National Academy Press (NAP) publishes over 200 books a year on a wide range of topics in science, engineering, and health, capturing the most authoritative views on important issues in science and health policy.', 'http://books.nap.edu/books/0309070317/html/177.html', 'http://search.nap.edu/nap-cgi/napsearch.cgi?term={$formKeywords}', NULL, 3),
(172, 33, 'Universal Library', 'The Universal Library is considerably more involved, and more ambitious -- it has amassed a collection of 100,000 e-books (some of them from Project Gutenberg), and is aiming for 1 million e-books by the end of 2006.', 'http://tera-3.ul.cs.cmu.edu/', 'http://tera-3.ul.cs.cmu.edu/cgi-bin/DBscripts/advsearch_db.cgi?perPage=25&listStart=0&author1=&subject1=Any&year1=&year2=&language1=Any&scentre=Any&search=Search&title1={$formKeywords}', NULL, 4),
(173, 34, 'Ingenta', 'Ingenta restricts access to full text articles. Although access to the full text costs money, the site can be used as a free index.', 'http://www.ingenta.com/', 'http://www.ingenta.com/isis/searching/Search/ingenta?database=1&title={$formKeywords}', NULL, 0),
(174, 34, 'ebrary', 'Independent researchers who do not have access to ebrary?s databases through their library may now set up an individual account for as little as $5. ebrary Discover spans multiple academic disciplines and provides anytime, anywhere access to over 20,000 authoritative titles including books in full-text, sheet music, reports and other authoritative documents from more than 175 leading publishers such as Cambridge University Press, Random House, Inc., and The McGraw-Hill Companies.', 'http://www.ebrary.com/corp/content.htm', 'http://shop.ebrary.com/Top?layout=search&f00=text&p01=&f01=subject&d=journal&l=en&sch=%A0%A0%A0%A0%A0Search%A0%A0%A0%A0%A0&frm=adv.x&p00={$formKeywords}', NULL, 1),
(175, 34, 'Questia', 'Questia provides access to over 47,000 books and 375,000 journal, magazine, and newspaper articles. All books and articles are available in their entirety - search every page. The subscription rate is ranging from 19.05 per month to 119.95 per year.', 'http://www.questia.com', 'http://www.questia.com/SM.qst?act=search&keywordsSearchType=1000&mediaType=book&mediaType=journal&mediaType=magazine&mediaType=newspaper&mediaType=encyclopedia&mediaType=startpage&keywords={$formKeywords}', NULL, 2),
(176, 34, 'TheScientificWorld', 'TheScientificWorld offers sciBASE to give free access to a collection of databases of scientific, technical and medical research literature. sciBASE now also features immediate digital delivery of full text articles from over 700 journals produced by participating publishers, and sciBASE is particularly effective for users who do not have library support (non-mediated environments).', 'http://www.thescientificworld.com/', 'http://www.thescientificworld.com/SCIENTIFICWORLDJOURNAL/search/SearchResults.asp?From=Main&Terms={$formKeywords}', NULL, 3),
(177, 34, 'Wiley InterScience Pay-per-view Service', 'Wiley InterScience Pay-per-view affords instant, full-text access to an extensive collection of journal articles or book chapters available on Wiley InterScience without the need for a subscription. This service allows anyone to purchase access to individual journal articles or book chapters directly by using a credit card. Access is instant and available for 24 hours.', 'http://www3.interscience.wiley.com/aboutus/ppv-articleselect.html', 'http://www3.interscience.wiley.com/search/allsearch', 'allContentSearchForm=&mode=quicksearch&WISindexid1=WISall&WISsearch1={$formKeywords}', 4),
(178, 35, 'Canadian Health Network', 'Canadian Health Network (Canada) The Canadian Health Network (CHN) is a growing network, bringing together the best information resources of leading Canadian and international health information.', 'http://www.canadian-health-network.ca/servlet/ContentServer?pagename=CHN-RCS/Page/HomePageTemplate&cid=1038611684536&c=Page&lang=En', 'http://www.canadian-health-network.ca/servlet/ContentServer?cid=1046357853421&pagename=CHN-RCS%2FPage%2FSearchPageTemplate&c=Page&lang=En&orderBy=ORDER_RANK&searchType=ALL_WORDS&logSearch=true&searchStr={$formKeywords}', NULL, 0),
(179, 35, 'Directgov', 'Directgov is replacing UK online as the place to turn to for the latest and widest range of public service information from the UK government.', 'http://www.direct.gov.uk/Homepage/fs/en', 'http://www.direct.gov.uk/AdvancedSearch/SearchResults/fs/en?NP=1&PO1=C&PI1=W&PF1=A&PG=1&RP=20&SC=__dgov_site&Z=1&PT1={$formKeywords}', NULL, 1),
(180, 35, 'HealthInsite', 'HealthInsite (Australia) aims to improve the health of Australians by providing easy access to quality information about human health.', 'http://www.healthinsite.gov.au', 'http://www.healthinsite.gov.au/search97cgi/s97_cgi?Action=FilterSearch&Filter=ve_quick_search_filter.hts&ResultErrorTemplate=ve_error.hts&ResultCount=10&ResultMaxDocs=600&gl_search_collection=full&searchtype=simple&collection=healthinsite_coll&SortSpec=Score+desc+VDKDate_Modified+desc+VdkMeta_Title+asc+vdktarget_comp_num+asc&ResultTemplate=ve_search_results_new.hts&gl_search_text={$formKeywords}', NULL, 2),
(181, 35, 'NHS Direct', 'NHS Direct (UK) is a gateway to health information on the Internet in the United Kingdom. NHS Direct Online provides health advice and is supported by a 24 hour nurse advice and information help line.', 'http://www.nhsdirect.nhs.uk', 'http://search.nhsdirect.nhs.uk/kbroker/nhsdirect/nhsdirect/search.lsim?hs=0&sm=0&ha=1054&sc=nhsdirect&mt=0&sb=0&nh=3&qt={$formKeywords}', NULL, 3),
(182, 35, 'PubMed Central', 'PubMed Central is a free online archive of full-text, peer-reviewed research papers in the life sciences.', 'http://pubmedcentral.nih.gov/', 'http://www.ncbi.nlm.nih.gov/entrez/query.fcgi?db=pmc&cmd=search&pmfilter_Fulltext=on&pmfilter_Relevance=on&term={$formKeywords}', NULL, 4),
(183, 36, 'Hardin Meta Directory of Internet Health Resources', 'List of lists compiled by the Hardin Library of the Health Sciences at the University of Iowa. Divided into longer, medium-sized and shorter lists of links. The Hardin MD Clean Bill of Health Award is given to the best sites that have connection rates of at least 93%.', 'http://www.lib.uiowa.edu/hardin/md/index.html', 'http://search.atomz.com/search/?sp-a=00050f6e-sp00000000&sp-q={$formKeywords}', NULL, 0),
(184, 36, 'AMEDEO', 'AMEDEO has been created to serve the needs of healthcare professionals.  AMEDEO''s core components include weekly emails with bibliographic lists about new scientific publications, personal Web pages for one-time download of available abstracts, and an overview of the medical literature published in relevant journals over the past 12 to 24 months.', 'http://www.amedeo.com', 'http://www.google.com/custom?sa=Search&sitesearch=www.amedeo.com&q={$formKeywords}', NULL, 1),
(185, 37, 'AMA Physician Select', 'This American Medical Association site provides addresses, specialties, education and other background information on licensed physicians in the U.S. and its possessions. Search by physician name or medical specialty.', 'http://www.ama-assn.org/aps/amahg.htm', 'http://search.ama-assn.org/Search/query.html?qc=public+amnews&qt={$formKeywords}', NULL, 0),
(186, 37, 'Diseases Databases Ver 1.6; Medical lists and links', 'Diseases Databases Ver 1.6: medical lists and links - a cross-referenced index of human disease, medications, symptoms, signs, abnormal investigation findings etc.; provides a medical textbook-like index and search portal.', 'http://www.diseasesdatabase.com/begin.asp?gif=1', 'http://www.diseasesdatabase.com/item_choice.asp? bytSearchType=0&strUserInput={$formKeywords}', NULL, 1),
(187, 37, 'Drugs.com', 'Drugs.com is a free resource for medical professionals and consumers providing convenient and reliable drug information. Drug monographs are presented at both professional (USPDI), and consumer levels. The drug information could be found by browsing the alphabetical listing of the drugs or by search using generic or trade name. The drug interaction guide allows check for drug-drug and drug-food interactions.', 'http://www.drugs.com/', 'http://www.drugs.com/search.php?is_main_search=1&searchterm={$formKeywords}', NULL, 2),
(188, 37, 'National Center for Biotechnology Information', 'In addition to maintaining the GenBank nucleic acid sequence database, the National Center for Biotechnology Information (NCBI) provides data analysis and retrieval and resources that operate on the data in GenBank and a varity of other biological data made available through NCBI''s Web site. NCBI data retrieval resources include Entrez, PubMed, LocusLink and the Taxonomy Browser. Data analysis resources include BLAST, Electronic PCR, OrfFinder, RefSeq, UniGene, Databases of Single Nucleotide Polymorphisms (dbSNP), Human Genome Sequencing pages, GeneMap ''99, Davis Human-Mouse Homology Map, Cancer Chromosome Abberation Project (CCAP) pages, Entrez Genomes, Clusters of Orthologous Groups (COGs) database, Retroviral Genotyping Tools, Cancer Genome Anatomy Project (CGAP) pages, SAGEmap, Online Mendelian Inheritance in Man (OMIM) and the Molecular Modeling Databases (MMDB).', 'http://www.ncbi.nlm.nih.gov/', 'http://www.ncbi.nlm.nih.gov/gquery/gquery.fcgi?term={$formKeywords}', NULL, 3),
(189, 37, 'RxList', 'RxList: The Internet drug index - Searchable cross index of US prescription products for both consumers and medical professionals', 'http://www.rxlist.com/', 'http://www.rxlist.com/cgi/rxlist.cgi?drug=acetaminophen', NULL, 4),
(190, 38, 'EurekAlert!', 'EurekAlert! is an online press service created by the American Association for the Advancement of Science (AAAS). The primary goal of EurekAlert! is to provide a forum where research institutions, universities, government agencies, corporations and the like can distribute science-related news to reporters and news media. The secondary goal of EurekAlert! is to archive these press releases and make them available to the public in an easily retrievable system.', 'http://www.eurekalert.org/links.php', 'http://search.eurekalert.org/e3/query.html?col=ev3rel&qc=ev3rel&qt={$formKeywords}', NULL, 0),
(191, 38, 'Mad Science Net: The 24-hour exploding laboratory', 'Mad Science Net: The 24-hour exploding laboratory is a collective cranium of scientists providing answers to your questions.', 'http://www.madsci.org/', 'http://www.madsci.org/cgi-bin/search?Submit=Submit+Query&or=AND&words=1&index=MadSci+Archives&MAX_TOTAL=25&area=All+areas&grade=All+grades&query={$formKeywords}', NULL, 1),
(192, 39, 'Science, Mathematics, Engineering and Technology Education (SMETE)', 'Science, Mathematics, Engineering and Technology Education (SMETE) contains a searchable working prototype of a National Science, Mathematics, Engineering, and Technology Education Digital Library.', 'http://www.smete.org/', 'http://www.smete.org/smete/?path=/public/find/search_results.jhtml&_DARGS=/smete/public/find/index_body.jhtml&/smete/forms/FindLearningObjects.operation=search&_D:/smete/forms/FindLearningObjects.operation=%20&_D:/smete/forms/FindLearningObjects.keyword=%20&/smete/forms/FindLearningObjects.learningResourceType=&_D:/smete/forms/FindLearningObjects.learningResourceType=%20&/smete/forms/FindLearningObjects.grade=0-Any&_D:/smete/forms/FindLearningObjects.grade=%20&/smete/forms/FindLearningObjects.title=&_D:/smete/forms/FindLearningObjects.title=%20&/smete/forms/FindLearningObjects.author=&_D:/smete/forms/FindLearningObjects.author=%20&/smete/forms/FindLearningObjects.hostCollection=&_D:/smete/forms/FindLearningObjects.hostCollection=%20&/smete/forms/FindLearningObjects.afterYear=&_D:/smete/forms/FindLearningObjects.afterYear=%20&/smete/forms/FindLearningObjects.beforeYear=&_D:/smete/forms/FindLearningObjects.beforeYear=%20&&/smete/forms/FindLearningObjects.keyword={$formKeywords}', NULL, 0),
(193, 40, 'Access to Australian Government Information and Services', 'The Australian Government Entry Point offers comprehensive and integrated access to Australian Government information and services.  You will find a concentrated body of Australian Government information through this site. <a href="http://www.australia.gov.au">www.australia.gov.au</a> currently signposts over 700 Australian Government web sites, indexes more than 5,000,000 pages and uses both metadata and full text indexing to ensure it is a comprehensive government portal.', 'http://www.fed.gov.au', 'http://govsearch.australia.gov.au/search/search.cgi?collection=gov&form=au&query=&query_phrase=&query_or=&query_not=&meta_f_sand=&scope=&fscope=512&num_ranks=20&chksummary=chksummary&query_and={$formKeywords}', NULL, 0);
INSERT INTO `rt_searches` (`search_id`, `context_id`, `title`, `description`, `url`, `search_url`, `search_post`, `seq`) VALUES
(194, 40, 'The Government of Canada', 'You can search across all government departments federal departments and agencies as well as provincial, territorial and municipal governments. There is a Departments and Agencies link, and the A to Z Index offers a keyword search to programmes and services. \n\nA recent development on Departmental sites is the inclusion of a "Proactive Disclosure" page, which outlines travel and hospitality expenses, disclosure of contracts, grants and awards. \n\nThe About Canada page includes links to Departments and Agencies, Structure of the Canadian Government, Commissions of Inquiry and more. \n\nIn addition to Department web sites, the government has been creating Portals which bring together information from across federal and provincial sources, and non-governmental organizations as well.', 'http://www.canada.gc.ca/main_e.html', 'http://search-recherche.gc.ca/cgi-bin/query?mss=canada%2Fen%2Fsimple&pg=q&enc=iso88591&site=main&bridge=&stt=&lowercaseq=&what=web&user=searchintranet&browser=N6E&kl=XX&op=a&q={$formKeywords}', NULL, 1),
(195, 40, 'Directgov', 'Directgov is a government service giving people access to the latest and widest range of public service information in one place on the Web and on Digital TV. Directgov uses the specifications formulated and consulted on through UK GovTalk.', 'http://www.direct.gov.uk', 'http://www.direct.gov.uk/AdvancedSearch/SearchResults/fs/en?NP=1&PO1=C&PI1=W&PF1=A&PG=1&RP=20&SC=__dgov_site&Z=1&PT1={$formKeywords}', NULL, 2),
(196, 40, 'Info4local.gov.uk', 'Information for local government from central government.  This site gives local authorities a quick and easy way to find relevant information on the web sites of central government departments, agencies and public bodies. It includes extensive information on implementing electronic government.', 'http://www.info4local.gov.uk', 'http://www.info4local.gov.uk/?view=Search+results&subjects=all&departmentsIds=0&informationTypesIds=all&query={$formKeywords}', NULL, 3),
(197, 40, 'The New Zealand government portal', 'The New Zealand government portal is a website providing search capability for, and links into the online and offline information and services of most government agencies.  This site is managed and maintained by the State Services Commission, a New Zealand government department.', 'http://www.govt.nz', 'http://www.govt.nz/search?type=spider&t=spider&q={$formKeywords}', NULL, 4),
(198, 40, 'Europa', 'Current information on the member countries of the European Union, including statistical data, policies, news, official documents, legal texts, and other publications.', 'http://europa.eu.int/index_en.htm', 'http://europa.eu.int/geninfo/query/resultaction.jsp?page=1', 'Collection=EuropaFull&ResultTemplate=/result_en.jsp&ResultCount=25&qtype=simple&ResultMaxDocs=200&DefaultLG=en&QueryText={$formKeywords}', 5),
(199, 40, 'The Global Legal Information Network (GLIN)', 'The Global Legal Information Network (GLIN) is a database of laws, regulations, judicial decisions, and other complementary legal sources contributed by governmental agencies and international organizations. These GLIN members contribute the official full texts of published documents to the database in their original language. Each document is accompanied by a summary in English and subject terms selected from the multilingual index to GLIN.', 'http://www.glin.gov', 'http://www.glin.gov/search.do?refineQuery=&offset=0&refineQueryType=&fromSearch=true&queryType=ALL&searchBtn=Search&includeAllFields=on&searchAll=on&sumLanguage=any&pubLanguage=any&pubJurisdiction=any&publicationJurisdictionExclude=false&searchPublicationDate=true&dateFromDay=01&dateFromMonth=01&dateFromYear=&dateToDay=01&dateToMonth=01&dateToYear=&subjTerm=&subjTermConjunction%5B0%5D=AND&subjTerm=&subjTermConjunction%5B1%5D=AND&subjTerm=&subjTermConjunction%5B2%5D=AND&subjTerm=&subjTermConjunction%5B3%5D=AND&subjTerm=&searchOrder=default&hitsPerPage=10&showSummary=on&queryString={$formKeywords}', NULL, 6),
(200, 40, 'Health Canada', 'Health Canada is the federal department responsible for helping the people of Canada maintain and improve their health.  Health Canada is committed to improving the lives of all of Canada''s people and to making this country''s population among the healthiest in the world as measured by longevity, lifestyle and effective use of the public health care system.', 'http://search.hc-sc.gc.ca/cgi-bin/query?mss=hcsearch', 'http://search.hc-sc.gc.ca/cgi-bin/query?mss=hcresult&pg=aq&enc=iso88591&ft=adverse+drug&doc=all&results=any&exclude=&r=&kl=en&subsite=both&search=Search&q={$formKeywords}', NULL, 7),
(201, 40, 'FirstGov', 'FirstGov (U.S. federal and state) is a public-private partnership, led by a cross-agency board.', 'http://www.firstgov.gov/', 'http://www.firstgov.gov/fgsearch/index.jsp?db=www&st=AS&ms0=should&mt0=all&rn=2&parsed=true&x=2&y=8&mw0={$formKeywords}', NULL, 8),
(202, 40, 'Canada Sites', 'Canada Sites provides an information and services gateway run by the Government of Canada and each of the provinces.', 'http://www.canada.gc.ca/main_e.html', 'http://search-recherche.gc.ca/cgi-bin/query?mss=canada%2Fen%2Fsimple.html&enc=iso88591&pg=q&kl=en&site=main&q={$formKeywords}', NULL, 9),
(203, 41, 'Google News', 'Search news reports using the popular search engine Google''s news feature.', 'http://news.google.com/', 'http://news.google.com/news?hl=en&q={$formKeywords}', NULL, 0),
(204, 41, 'Nature Science Update', 'Nature Science Update provides daily news stories written by the editors of the Nature Journal.', 'http://www.nature.com/nsu/', 'http://search.nature.com/search/?sp-x-9=cat&sp-q-9=NEWS&submit=go&sp-a=sp1001702d&sp-sfvl-field=subject%7Cujournal&sp-t=results&sp-x-1=ujournal&sp-p-1=phrase&sp-p=all&sp-q={$formKeywords}', NULL, 1),
(205, 41, 'The Scientist', 'The Scientist is the online resource for the printed magazine, The Scientist. Provides access to information useful to those working in or studying the life sciences.', 'http://www.the-scientist.com/', 'http://www.the-scientist.com/search/dosearch/', 'box_type=toolbar&search_restriction=all&order_by=date&search_terms={$formKeywords}', 2),
(206, 41, 'New York Times', 'New York Times, New York (last seven days)', 'http://www.nytimes.com/', 'http://query.nytimes.com/search/query?date=site1week&submit.x=1&submit.y=9&query={$formKeywords}', NULL, 3),
(207, 41, 'Scientific American Archive', 'Scientific American Archive is the online science and technology resource offering access to every page and every issue of Scientific American magazine from 1993 to the present.', 'http://www.sciamdigital.com/', 'http://www.sciamdigital.com/index.cfm?fa=Search.ViewSearchForItemResultList&AUTHOR_CHAR=&TITLE_CHAR=&FullText_CHAR={$formKeywords}', NULL, 4),
(208, 41, 'Newsdirectory', 'Newsdirectory is a comprehensive and searchable guide to the world''s English-language online media. Currently over 8,000 newspapers and magazines are listed, as well as more than 1,000 U.S. television broadcasters.', 'http://www.newsdirectory.com/', 'http://www.newsdirectory.com/hbSearch.php', 's={$formKeywords}& submit=Go', 5),
(209, 42, 'Google', 'Search using the popular Google search engine.', 'http://www.google.com/', 'http://www.google.com/search?q={$formKeywords}', NULL, 0),
(210, 42, 'Google Scholar', 'Google Scholar enables specific searches of scholarly literature, including peer-reviewed papers, theses, books, pre-prints, abstracts, and technical reports. Content includes a range of publishers and aggregators with whom Google already has standing arrangements, e.g., the Association for Computing Machinery, IEEE, OCLC''s Open WorldCat library locator service, etc. Result displays will show different version clusters, citation analysis, and library location (currently books only).', 'http://scholar.google.com', 'http://scholar.google.com/scholar?ie=UTF-8&oe=UTF-8&hl=en&q={$formKeywords}', NULL, 1),
(211, 42, 'Clusty the Clustering Engine', 'This search tool from Vivismo offers clustered results for a selection of searches.  Metasearch the whole web, or use tabs to search for news, gossip, images, orproducts via Bizrate or eBay.', 'http://clusty.com/about', 'http://clusty.com/search?query={$formKeywords}', NULL, 2),
(212, 42, 'Vivisimo', 'The award-winning Vivisimo clustering technology, developed by Carnegie Mellon research scientists, is unlocking the value of stored information at Fortune 500 companies, top websites, prestigious publishers and government agencies by categorizing research results on the fly into meaningful orders, thus achieving breakthrough improvement in access to relevant information.', 'http://vivisimo.com', 'http://vivisimo.com/search?tb=homepage&v%3Asources=Web&query={$formKeywords}', NULL, 3),
(213, 43, 'Google Scholar', 'Google Scholar enables specific searches of scholarly literature, including peer-reviewed papers, theses, books, pre-prints, abstracts, and technical reports. Content includes a range of publishers and aggregators with whom Google already has standing arrangements, e.g., the Association for Computing Machinery, IEEE, OCLC''s Open WorldCat library locator service, etc. Result displays will show different version clusters, citation analysis, and library location (currently books only).', 'http://scholar.google.com', 'http://scholar.google.com/scholar?ie=UTF-8&oe=UTF-8&hl=eng&as_sauthors={$formKeywords}', NULL, 0),
(214, 43, 'Intute: Science, Engineering & Technology', 'Intute: Science, Engineering & Technology is a free online service providing you with access to the very best Web resources for education and research, evaluated and selected by a network of subject specialists. It covers the physical sciences, engineering, computing, geography, mathematics and environmental science. The database currently contains 33349 records.', 'http://www.intute.ac.uk/sciences/', 'http://www.intute.ac.uk/sciences/cgi-bin/search.pl?submit.x=0&submit.y=0&submit=Go&limit=0&subject=sciences&term1={$formKeywords}', NULL, 1),
(215, 43, 'OAIster (Open Archives Initiative research databases)', 'OAIster is a project of the University of Michigan Digital Library Production Services, and provides searching a wide variety of collections from a wide variety of institutions. These institutions have made the records of their digital resources available to access, and the project team has gathered and aggregated them into the OAIster service.', 'http://oaister.umdl.umich.edu/', 'http://oaister.umdl.umich.edu/cgi/b/bib/bib-idx?type=boolean&size=10&c=oaister&q1=&rgn1=entire+record&op2=and&q2=&rgn2=entire+record&op3=And&q3=&rgn3=title&op4=And&rgn4=author&op5=And&q5=&rgn5=subject&op6=And&rgn6=norm&q6=all+types&sort=author%2Fcreator&submit2=search&q4={$formKeywords}', NULL, 2),
(216, 43, 'DOE Information Bridge', 'The Information Bridge provides the open source to full-text and bibliographic records of Department of Energy (DOE) research and development reports in physics, chemistry, materials, biology, environmental sciences, energy technologies, engineering, computer and information science, renewable energy, and other topics.', 'http://www.osti.gov/bridge/index.jsp', 'http://www.osti.gov/bridge/basicsearch.jsp?act=Search&formname=basicsearch.jsp&review=1&SortBy=RELV&SortOrder=DESC&querytype=search&searchFor={$formKeywords}', NULL, 3),
(217, 43, 'Public Knowledge Project Open Archives Harvester', 'The PKP Open Archives Harvester is a free metadata indexing system developed by the Public Knowledge Project through its federally funded efforts to expand and improve access to research. The PKP OAI Harvester allows you to create a searchable index of the metadata from Open Archives Initiative-compliant archives, such as sites using Open Journal Systems or Open Conference Systems.', 'http://pkp.sfu.ca/harvester/', 'http://pkp.sfu.ca/harvester/search.php?limit=author&query={$formKeywords}', NULL, 4),
(218, 43, 'National Environmental Publications Internet Site (NEPIS)', 'The National Environmental Publications Information System began in 1997, to offer over 9,000 full text, online documents of the United States Environmental Protection Agency. Documents that are not available online can be ordered from the agency through NEPIS.', 'http://nepis.epa.gov/', 'http://nepis.epa.gov/Exe/ZyNET.exe?User=ANONYMOUS&Password=anonymous&Client=EPA&SearchBack=ZyActionL&SortMethod=h&SortMethod=-&MaximumDocuments=15&Display=hpfr&ImageQuality=r85g16%2Fr85g16%2Fx150y150g16%2Fi500&DefSeekPage=x&ZyAction=ZyActionS&Toc=&TocEntry=&QField=&QFieldYear=&QFieldMonth=&QFieldDay=&UseQField=&Docs=&IntQFieldOp=0&ExtQFieldOp=0&File=&SeekPage=&Back=ZyActionL&BackDesc=Contents+page&MaximumPages=1&ZyEntry=0&TocRestrict=n&SearchMethod=1&Time=&FuzzyDegree=0&Index=National+Environmental+Publications+Info&Query={$formKeywords}', NULL, 5),
(219, 44, 'Google', 'Search for definitions using the popular Google search engine.', 'http://www.google.com/', 'http://www.google.com/search?hl=en&q=define%3A{$formKeywords}', NULL, 0),
(220, 44, 'NAL Agricultural Thesaurus', 'NAL Agricultural Thesaurus was created to meet the needs of the United States Department of Agriculture (USDA), Agricultural Research Service (ARS), for an agricultural thesaurus. NAL Agricultural Thesaurus is for anyone describing, organizing and classifying agricultural resources such as: books, articles, catalogs, databases, patents, games, educational materials, pictures, slides, film, videotapes, software, other electronic media, or websites. It is organized into 17 subject categories.', 'http://agclass.nal.usda.gov/agt/agt.htm', 'http://search.nal.usda.gov/query.html?charset=iso-8859-1&ht=0&qp=&qs=-url%3Atektran&qc=-&pw=100%25&ws=0&la=en&qm=0&st=1&nh=10&lk=1&rf=0&oq=&rq=0&si=1&x=15&y=8&qt={$formKeywords}', NULL, 1),
(221, 44, 'Merriam-Webster Online', 'A dictionary based on the Webster 10th Collegiate edition, 1993, with updates added annually. More than 160,000 entries. Each word is accompanied by definition, pronunciation, usage, grammatical function, and a brief etymology. Includes a thesaurus. Misspellings return suggested spellings. The dictionary''s search system supports internal wildcards and right hand truncation searching. Includes an extensive pronunciation guide and sound files for the pronunciation of many terms.', 'http://www.merriam-webster.com/', 'http://www.merriam-webster.com/cgi-bin/dictionary?book=Dictionary&va={$formKeywords}', NULL, 2),
(222, 44, 'OneLook Dictionaries', 'OneLook is a meta-dictionary. Enter an English language word or acronym, and OneLook will search its index of 5,292,362 words in 934 dictionaries indexed in general and special interest dictionaries for the definition.', 'http://www.onelook.com/index.html', 'http://www.onelook.com/?ls=a&w={$formKeywords}', NULL, 3),
(223, 44, 'Wikipedia: The Free Encyclopedia', '"Wikipedia is a free content encyclopedia that is being written collaboratively by contributors from all around the world. The site is a WikiWiki, meaning that anyone ... can edit any article. ..." Without editorial oversight, the quality of the content on this site varies dramatically, but it is worth exploring. The English version has hundreds of thousands of entries. Spanish, French, and Esperanto are among the several other languages offered.', 'http://en.wikipedia.org/wiki/Main_Page', 'http://en.wikipedia.org/wiki/Special:Search?go=Go&fulltext=Search&search={$formKeywords}', NULL, 4),
(224, 44, 'Infoplease.com', 'This site includes contents of the Information Please Almanacs, a biography database, a dictionary, an atlas, and articles from the Columbia Encyclopedia. You can search these ready reference works together or separately or browse the Almanacs. There are feature stories each week covering topics in the news, with links to pertinent Almanac content and other Web resources.', 'http://www.infoplease.com', 'http://www.infoplease.com/search?fr=iptn&in=all&x=0&y=0&query={$formKeywords}', NULL, 5),
